--------------------------------------------------------
--  DDL for Package WSH_ITM_RESPONSE_PKG
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE PACKAGE "APPS"."WSH_ITM_RESPONSE_PKG" AUTHID CURRENT_USER AS
/* $Header: WSHITRAS.pls 120.0.12010000.1 2008/07/29 06:14:48 appldev ship $ */
--  Package
--      WSH_CC_RESPONSE_PKG
--
--  Purpose
--      Spec of package WSH_ITM_RESPONSE_PKG. This package is used for
--      interpreting the response generated by ITM Adapter for a request.
--
--  History
--

  TYPE SrvTyp IS RECORD
   (
     Service_Type VARCHAR2(20),
     Service_Result VARCHAR2(1)
   );

  TYPE SrvTabTyp IS TABLE OF SrvTyp INDEX BY BINARY_INTEGER;

  -- Name
  --   ONT_RESPONSE_ANALYSER
  -- Purpose
  --   On passing the transaction_control_id of a request this package returns
  --   the interpreted status of the request based on the rules defined
  -- Arguments
  --   request_control_id          request_control_id of a request
  --   x_interpreted_value         Interpreted Value
  --   x_SrvTab                    PL/SQL Table
  --   x_return_status             Return status of the API-SUCCESS/ERROR
  -- Notes
  --   Refer the record T_RESULT_STATUS_REC


PROCEDURE ONT_RESPONSE_ANALYSER
  (
    p_request_control_id IN  NUMBER,
    x_interpreted_value  OUT NOCOPY  VARCHAR2,
    x_SrvTab             OUT NOCOPY  WSH_ITM_RESPONSE_PKG.SrvTabTyp,
    x_return_status      OUT NOCOPY  VARCHAR2
   );

PROCEDURE ONT_RESPONSE_ANALYSER
  (
    p_reference_id	IN  NUMBER,
    p_reference_line_id IN  NUMBER,
    x_interpreted_value  OUT NOCOPY  VARCHAR2,
    x_SrvTab             OUT NOCOPY  WSH_ITM_RESPONSE_PKG.SrvTabTyp,
    x_return_status      OUT NOCOPY  VARCHAR2
   );

END WSH_ITM_RESPONSE_PKG;

/
