   ASO_QUOTE_UTIL_PVT.Set_Copy_Quote_Control_Rec_W(
      p_copy_header_only        => p_cc_copy_header_only,
      p_new_version             => p_cc_new_version,
      p_copy_note               => p_cc_copy_note,
      p_copy_task               => p_cc_copy_task,
      p_copy_attachment         => p_cc_copy_attachment,
      p_pricing_request_type    => p_cc_pricing_request_type,
      p_header_pricing_event    => p_cc_header_pricing_event,
      p_price_mode              => p_cc_price_mode        ,
      p_calc_freight_charge_flag => p_cc_calc_freight_charge_flag,
      p_calculate_tax_flag      => p_cc_calculate_tax_flag        ,
      x_copy_quote_control_rec  => l_copy_qte_control_rec);

   -- Call ASO API
   IF l_debug = 'Y' THEN
     ASO_QUOTE_UTIL_PVT.Debug('Begin calling ASO_Copy_Quote_Pub.Copy_Quote');
   END IF;
   ASO_Copy_Quote_Pub.Copy_Quote(
      p_api_Version_Number      => 1.0,
      p_init_msg_list           => FND_API.G_FALSE,
      p_commit                  => FND_API.G_FALSE,
      p_copy_quote_header_rec   => l_copy_qte_hdr_rec,
      p_copy_quote_control_rec  => l_copy_qte_control_rec,
      x_qte_header_id           => x_quote_header_id,
      x_qte_number              => x_quote_number,
      x_return_status           => x_return_status,
      x_msg_count               => x_msg_count,
      x_msg_data                => x_msg_data);
   IF l_debug = 'Y' THEN
     ASO_QUOTE_UTIL_PVT.Debug('Returning from ASO_Copy_Quote_Pub.Copy_Quote');
   END IF;

   IF FND_API.To_Boolean( p_commit ) THEN
      COMMIT WORK;
   END IF;

   IF l_debug = 'Y' THEN
     ASO_QUOTE_UTIL_PVT.debug('ASO_QUOTE_PUB_W.copy_quote(+)');
   END IF;
   -- standard call to get message count and if count is 1, get message info.
   FND_MSG_PUB.Count_And_Get
   (   p_encoded => FND_API.G_FALSE,
       p_count => x_msg_count,
       p_data  => x_msg_data
    );

   IF l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
   END IF;

   EXCEPTION
      WHEN FND_API.G_EXC_ERROR THEN
         ROLLBACK TO Copy_Quote_Pvt;
         x_return_status := FND_API.G_RET_STS_ERROR;
         FND_MSG_PUB.Count_And_Get
         (   p_encoded => FND_API.G_FALSE,
             p_count => x_msg_count,
             p_data  => x_msg_data
         );
         IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
         END IF;
      WHEN FND_API.G_EXC_UNEXPECTED_ERROR THEN
         ROLLBACK TO Copy_Quote_Pvt;
         x_return_status := FND_API.G_RET_STS_UNEXP_ERROR;
         FND_MSG_PUB.Count_And_Get
         (   p_encoded => FND_API.G_FALSE,
             p_count => x_msg_count,
             p_data  => x_msg_data
         );
         IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
         END IF;
      WHEN OTHERS THEN
         ROLLBACK TO Copy_Quote_Pvt;
         x_return_status := FND_API.G_RET_STS_UNEXP_ERROR;
         FND_MESSAGE.Set_Name('FND', 'SQL_PLSQL_ERROR');
         FND_MESSAGE.Set_Token('ROUTINE', l_api_name);
         FND_MESSAGE.Set_Token('ERRNO', SQLCODE);
         FND_MESSAGE.Set_Token('REASON', SQLERRM);
         FND_MSG_PUB.Add;
         IF FND_MSG_PUB.Check_Msg_Level
            (FND_MSG_PUB.G_MSG_LVL_UNEXP_ERROR)
         THEN FND_MSG_PUB.Add_Exc_Msg
            (G_PKG_NAME,
             l_api_name
            );
         END IF;
         FND_MSG_PUB.Count_And_Get
         (   p_encoded => FND_API.G_FALSE,
             p_count => x_msg_count,
             p_data  => x_msg_data
         );
         IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
         END IF;
END Copy_Quote;

