   p_qty_tbl              IN  JTF_NUMBER_TABLE,
   p_uom_code_tbl         IN  JTF_VARCHAR2_TABLE_100,
   x_valid_qty_tbl        OUT NOCOPY JTF_VARCHAR2_TABLE_100
) is

   l_api_name    CONSTANT VARCHAR2(30) 	:= 'validate_quantity';
   l_api_version CONSTANT NUMBER		:= 1.0;
   l_output_qty  NUMBER;
   l_primary_qty NUMBER;
   l_debug       VARCHAR2(1);
begin

   l_debug := ASO_QUOTE_UTIL_PVT.is_debug_enabled;
   IF l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.Enable_Debug_Pvt;
   END IF;
   -- standard call to check for call compatibility
   IF NOT FND_API.Compatible_API_Call (l_api_version,
                                       p_api_version,
                                       l_api_name,
                                       G_PKG_NAME   )
   THEN
      RAISE FND_API.G_EXC_UNEXPECTED_ERROR;
   END IF;

   -- initialize message list if p_init_msg_list is set to TRUE
   IF FND_API.to_Boolean(p_init_msg_list) THEN
      FND_MSG_PUB.initialize;
   END IF;

   -- initialize API return status to success
   x_return_status := FND_API.G_RET_STS_SUCCESS;

   IF l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.debug('ASO_QUOTE_PUB_W.validate_quantity(+)');
   END IF;

   -- begin API body

   x_valid_qty_tbl := JTF_VARCHAR2_TABLE_100();
   x_valid_qty_tbl.extend(p_item_id_tbl.count);

   for i in 1..p_item_id_tbl.count loop
      IF l_debug = 'Y' THEN
         ASO_QUOTE_UTIL_PVT.debug('Begin calling INV_DECIMALS_PUB.validate_quantity ' || TO_CHAR(SYSDATE,'DD-MON-YYYY:HH24:MI:SS'));
      END IF;
      inv_decimals_pub.validate_quantity(p_item_id_tbl(i), p_organization_id_tbl(i), p_qty_tbl(i),
                                         p_uom_code_tbl(i), l_output_qty, l_primary_qty, x_valid_qty_tbl(i));
      IF l_debug = 'Y' THEN
         ASO_QUOTE_UTIL_PVT.debug('Returning from INV_DECIMALS_PUB.validate_quantity ' || TO_CHAR(SYSDATE,'DD-MON-YYYY:HH24:MI:SS'));
      END IF;
   end loop;

   -- end API body
   IF l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.debug('ASO_QUOTE_PUB_W.validate_quantity(-)');
   END IF;

   -- standard call to get message count and if count is 1, get message info.
   FND_MSG_PUB.Count_And_Get
   (   p_encoded => FND_API.G_FALSE,
       p_count => x_msg_count,
       p_data  => x_msg_data
    );
   If l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
   END IF;

   EXCEPTION
      WHEN FND_API.G_EXC_ERROR THEN
         x_return_status := FND_API.G_RET_STS_ERROR;
         FND_MSG_PUB.Count_And_Get
         (   p_encoded => FND_API.G_FALSE,
             p_count => x_msg_count,
             p_data  => x_msg_data
         );
         IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
         END IF;
      WHEN FND_API.G_EXC_UNEXPECTED_ERROR THEN
         x_return_status := FND_API.G_RET_STS_UNEXP_ERROR;
         FND_MSG_PUB.Count_And_Get
         (   p_encoded => FND_API.G_FALSE,
             p_count => x_msg_count,
             p_data  => x_msg_data
         );
         IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
         END IF;
      WHEN OTHERS THEN
         x_return_status := FND_API.G_RET_STS_UNEXP_ERROR;
         FND_MESSAGE.Set_Name('FND', 'SQL_PLSQL_ERROR');
         FND_MESSAGE.Set_Token('ROUTINE', l_api_name);
         FND_MESSAGE.Set_Token('ERRNO', SQLCODE);
         FND_MESSAGE.Set_Token('REASON', SQLERRM);
         FND_MSG_PUB.Add;
         IF FND_MSG_PUB.Check_Msg_Level
            (FND_MSG_PUB.G_MSG_LVL_UNEXP_ERROR)
         THEN FND_MSG_PUB.Add_Exc_Msg
            (G_PKG_NAME,
             l_api_name
            );
         END IF;
         FND_MSG_PUB.Count_And_Get
         (   p_encoded => FND_API.G_FALSE,
             p_count => x_msg_count,
             p_data  => x_msg_data
         );
         IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
         END IF;
