PROCEDURE Update_Object_Relationship (
  p_api_version                     IN  NUMBER      := 1.0,
  p_init_msg_list                   IN  VARCHAR2    := FND_API.G_FALSE,
  p_commit                          IN  VARCHAR2    := FND_API.G_FALSE,
  p_validation_level                IN  NUMBER      := FND_API.G_VALID_LEVEL_FULL,
  p_operation_code                  IN  VARCHAR2    := FND_API.G_MISS_CHAR,
  p_qro_related_object_id           IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_creation_date               IN  DATE        := FND_API.G_MISS_DATE,
  p_qro_created_by                  IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_last_update_date            IN  DATE        := FND_API.G_MISS_DATE,
  p_qro_last_updated_by             IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_last_update_login           IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_request_id                  IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_program_application_id      IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_program_id                  IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_program_update_date         IN  DATE        := FND_API.G_MISS_DATE,
  p_qro_quote_object_type_code      IN  VARCHAR2    := FND_API.G_MISS_CHAR,
  p_qro_quote_object_id             IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_object_type_code            IN  VARCHAR2    := FND_API.G_MISS_CHAR,
  p_qro_object_id                   IN  NUMBER      := FND_API.G_MISS_NUM,
  p_qro_relationship_type_code      IN  VARCHAR2    := FND_API.G_MISS_CHAR,
  p_qro_reciprocal_flag             IN  VARCHAR2    := FND_API.G_MISS_CHAR,
  p_qro_quote_object_code           IN  NUMBER      := FND_API.G_MISS_NUM,
  x_related_object_id               OUT NOCOPY NUMBER,
  x_return_status                   OUT NOCOPY VARCHAR2,
  x_msg_count                       OUT NOCOPY NUMBER,
  x_msg_data                        OUT NOCOPY VARCHAR2
)
AS
  CURSOR C_Rel_Obj_Last_Upd_Date (c_related_object_id NUMBER)
  IS
    SELECT last_update_date
    FROM   ASO_QUOTE_RELATED_OBJECTS
    WHERE  related_object_id = c_related_object_id;

  l_api_name        VARCHAR2(30) := 'Update_Object_Relationship';
  l_api_version     NUMBER   := 1.0;
  l_related_obj_rec ASO_QUOTE_PUB.RELATED_OBJ_REC_TYPE
    := ASO_QUOTE_PUB.G_MISS_RELATED_OBJ_REC;

  l_debug           VARCHAR2(1);
BEGIN

  l_debug := ASO_QUOTE_UTIL_PVT.is_debug_enabled;
  IF l_debug = 'Y' THEN
     ASO_QUOTE_UTIL_PVT.Enable_Debug_Pvt;
  END IF;

  -- Standard call to check for call compatibility
  IF NOT FND_API.Compatible_Api_Call(
    l_api_version,
    p_api_version,
    l_api_name,
    G_PKG_NAME)
  THEN
    RAISE FND_API.G_EXC_UNEXPECTED_ERROR;
  END IF;

  -- Initialize message list if p_init_msg_list is set to FND_API.G_TRUE
  IF FND_API.To_Boolean(p_init_msg_list)
  THEN
    FND_MSG_PUB.Initialize;
  END IF;

  -- Initialize API return status to SUCCESS
  x_return_status := FND_API.G_RET_STS_SUCCESS;

  -- populate l_related_obj_rec from IN parameters
  l_related_obj_rec.related_object_id := p_qro_related_object_id;
  l_related_obj_rec.creation_date := p_qro_creation_date;
  l_related_obj_rec.created_by := p_qro_created_by;
  l_related_obj_rec.last_update_date := p_qro_last_update_date;
  l_related_obj_rec.last_updated_by := p_qro_last_updated_by;
  l_related_obj_rec.last_update_login := p_qro_last_update_login;
  l_related_obj_rec.request_id := p_qro_request_id;
  l_related_obj_rec.program_application_id := p_qro_program_application_id;
  l_related_obj_rec.program_id := p_qro_program_id;
  l_related_obj_rec.program_update_date := p_qro_program_update_date;
  l_related_obj_rec.quote_object_type_code := p_qro_quote_object_type_code;
  l_related_obj_rec.quote_object_id := p_qro_quote_object_id;
  l_related_obj_rec.object_type_code := p_qro_object_type_code;
  l_related_obj_rec.object_id := p_qro_object_id;
  l_related_obj_rec.relationship_type_code := p_qro_relationship_type_code;
  l_related_obj_rec.reciprocal_flag := p_qro_reciprocal_flag;
  l_related_obj_rec.quote_object_code := p_qro_quote_object_code;

  -- Call different APIs to create/update/delete object relationship
  -- based on operation code
  IF p_operation_code = 'CREATE'
  THEN
    -- Call ASO API
    IF l_debug = 'Y'
    THEN ASO_QUOTE_UTIL_PVT.Debug('Begin calling ASO_RLTSHIP_PUB.Create_Object_Relationship');
    END IF;
    ASO_RLTSHIP_PUB.Create_Object_Relationship(
      P_Api_Version_Number => p_api_version,
      P_Init_Msg_List      => p_init_msg_list,
      P_Commit             => p_commit,
      P_Validation_Level   => p_validation_level,
      P_Related_Obj_Rec    => l_related_obj_rec,
      X_Related_Object_Id  => x_related_object_id,
      X_Return_Status      => x_return_status,
      X_Msg_Count          => x_msg_count,
      X_Msg_Data           => x_msg_data
    );
    IF l_debug = 'Y'
    THEN ASO_QUOTE_UTIL_PVT.Debug('Returning from ASO_RLTSHIP_PUB.Create_Object_Relationship');
    END IF;

