PROCEDURE GET_DEFAULT_PARTY_SITES (
     x_party_site_ids     OUT NOCOPY   JTF_NUMBER_TABLE,
     p_party_id           IN NUMBER,
     p_site_use_types     IN JTF_VARCHAR2_TABLE_100 :=NULL,
     p_api_version_number IN NUMBER,
     p_init_msg_list      IN VARCHAR2,
     x_return_status      OUT NOCOPY  VARCHAR2,
     x_msg_count          OUT NOCOPY  NUMBER,
     x_msg_data           OUT NOCOPY  VARCHAR2) AS

    -- Get the primary address
     CURSOR  c_primary_address(p_party_id NUMBER, p_site_use_type VARCHAR2) IS
      SELECT   hps.party_site_id
      FROM     hz_party_sites hps, hz_party_site_uses hpsu
      WHERE    hps.party_id= p_party_id
      AND      hps.status='A'
      AND      hps.party_site_id= hpsu.party_site_id
      AND      hpsu.site_use_type= p_site_use_type
      AND      hpsu.primary_per_type='Y'
      AND      hpsu.status='A';

    --Identifying address for the party id
     CURSOR c_identifying_address(p_party_id NUMBER) IS
      SELECT party_site_id
      FROM   hz_party_sites
      WHERE  party_id= p_party_id
      AND    status='A'
      AND    identifying_address_flag='Y';

     --Cursor for  to get the organization
     CURSOR c_organization(p_party_id NUMBER) IS
      SELECT  hrel.object_id
      FROM    hz_relationships hrel, hz_parties hp
      WHERE   hrel.party_id= p_party_id
      AND     hrel.object_id= hp.party_id
      AND     hp.party_type= 'ORGANIZATION';

      l_return_status VARCHAR2(5);
      l_count NUMBER :=0;
      l_index NUMBER :=0;

      l_party_site_id NUMBER;
      l_party_id      NUMBER;
      l_api_version  NUMBER := 1.0;
      l_api_name VARCHAR2(50) := 'GET_DEFAULT_PARTY_SITES';
      l_debug    VARCHAR2(1);
 BEGIN
    -- Enable  debug message
      l_debug := ASO_QUOTE_UTIL_PVT.is_debug_enabled;
      IF l_debug = 'Y' THEN
         ASO_QUOTE_UTIL_PVT.Enable_Debug_Pvt;
      END IF;

    -- Standard Start of API savepoint
      SAVEPOINT GET_DEFAULT_PARY_SITES;

      -- Standard call to check for call compatibility.
      IF NOT FND_API.Compatible_API_Call ( l_api_version,
                           	               p_api_version_number,
                                           l_api_name,
                                           G_PKG_NAME)
      THEN
         RAISE FND_API.G_EXC_UNEXPECTED_ERROR;
      END IF;

      -- Initialize message list if p_init_msg_list is set to TRUE.
      IF FND_API.to_Boolean( p_init_msg_list ) THEN
          FND_MSG_PUB.initialize;
      END IF;

      -- Debug Message
      IF l_debug = 'Y' THEN
         ASO_UTILITY_PVT.Debug_Message(FND_MSG_PUB.G_MSG_LVL_DEBUG_LOW,
                                       'Private API: ' || l_api_name || 'start');
      END IF;

      -- Initialize API return status to SUCCESS
      x_return_status := FND_API.G_RET_STS_SUCCESS;

      -- ******************************************************************
      -- Validate Environment
      -- ******************************************************************
      IF FND_GLOBAL.User_Id IS NULL THEN
        IF FND_MSG_PUB.Check_Msg_Level (FND_MSG_PUB.G_MSG_LVL_ERROR)THEN
              FND_MESSAGE.Set_Name(' + appShortName +',
                                   'UT_CANNOT_GET_PROFILE_VALUE');
              FND_MESSAGE.Set_Token('PROFILE', 'USER_ID', FALSE);
              FND_MSG_PUB.ADD;
          END IF;
          RAISE FND_API.G_EXC_ERROR;
      END IF;

	 x_party_site_ids    := JTF_NUMBER_TABLE();
     x_party_site_ids.extend(p_site_use_types.COUNT);

     FOR i in  p_site_use_types.FIRST..p_site_use_types.LAST LOOP
      l_index := l_index + 1;
      FOR c_getPrimaryAddress IN c_primary_address(p_party_id,p_site_use_types(l_index)) LOOP
       l_party_site_id := c_getPrimaryAddress.party_site_id;
      END LOOP;
      IF (l_party_site_id IS NULL) THEN
       FOR c_getIdentifyAddress IN c_identifying_address(p_party_id) LOOP
        l_party_site_id := c_getIdentifyAddress.party_site_id;
       END LOOP;
      END IF;
      IF (l_party_site_id IS NULL)  THEN
       FOR c_getOrganization in c_organization(p_party_id) LOOP
        l_party_id := c_getOrganization.object_id;
       END LOOP;
       IF (l_party_id IS NOT NULL) THEN
        FOR c_getPrimaryAddress1 IN c_primary_address(l_party_id,p_site_use_types(l_index)) LOOP
         l_party_site_id := c_getPrimaryAddress1.party_site_id;
        END LOOP;
        IF (l_party_site_id IS NULL)  THEN
         FOR c_getIdentifyAddress IN c_identifying_address(l_party_id) LOOP
          l_party_site_id := c_getIdentifyAddress.party_site_id;
         END LOOP;
        END IF;
       END IF;
      END IF;
      x_party_site_ids(i) := l_party_site_id;
      l_party_site_id :=NULL;
     END LOOP;

    -- Standard call to get message count and if count is 1, get message info.
    FND_MSG_PUB.Count_And_Get
      (  p_count          =>   x_msg_count,
         p_data           =>   x_msg_data
      );
    --disable the  debug message
    IF l_debug = 'Y' THEN
       ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
    END IF;
 EXCEPTION
    WHEN FND_API.G_EXC_ERROR THEN
       ASO_UTILITY_PVT.HANDLE_EXCEPTIONS(
               P_API_NAME => L_API_NAME
              ,P_PKG_NAME => G_PKG_NAME
              ,P_EXCEPTION_LEVEL => FND_MSG_PUB.G_MSG_LVL_ERROR
              ,P_PACKAGE_TYPE => ASO_UTILITY_PVT.G_PUB
              ,X_MSG_COUNT => X_MSG_COUNT
              ,X_MSG_DATA => X_MSG_DATA
              ,X_RETURN_STATUS => X_RETURN_STATUS);
      IF l_debug = 'Y' THEN
         ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
      END IF;
    WHEN FND_API.G_EXC_UNEXPECTED_ERROR THEN
        ASO_UTILITY_PVT.HANDLE_EXCEPTIONS(
               P_API_NAME => L_API_NAME
              ,P_PKG_NAME => G_PKG_NAME
              ,P_EXCEPTION_LEVEL => FND_MSG_PUB.G_MSG_LVL_UNEXP_ERROR
              ,P_PACKAGE_TYPE => ASO_UTILITY_PVT.G_PUB
              ,X_MSG_COUNT => X_MSG_COUNT
              ,X_MSG_DATA => X_MSG_DATA
              ,X_RETURN_STATUS => X_RETURN_STATUS);
       IF l_debug = 'Y' THEN
          ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
       END IF;
     WHEN OTHERS THEN
         ASO_UTILITY_PVT.HANDLE_EXCEPTIONS(
               P_API_NAME => L_API_NAME
              ,P_PKG_NAME => G_PKG_NAME
              ,P_EXCEPTION_LEVEL => ASO_UTILITY_PVT.G_EXC_OTHERS
              ,P_PACKAGE_TYPE => ASO_UTILITY_PVT.G_PUB
              ,X_MSG_COUNT => X_MSG_COUNT
              ,X_MSG_DATA => X_MSG_DATA
              ,X_RETURN_STATUS => X_RETURN_STATUS);
         IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.disable_debug_pvt;
         END IF;
 END GET_DEFAULT_PARTY_SITES;

