  /* Calculates the rollup quote price for a configured model item */
  FUNCTION Get_Model_Rollup_Quote_Price (p_quote_line_id NUMBER)
    RETURN NUMBER
  IS
    CURSOR C_Model_Config_Info (pc_quote_line_id IN NUMBER)
    IS
      SELECT config_header_id,
             config_revision_num
        FROM ASO_QUOTE_LINE_DETAILS
       WHERE quote_line_id = pc_quote_line_id
    ;

    /* declare a cursor to retrieve the rollup quote price of a model item */
    CURSOR C_Model_Rollup_Quote_Price (pc_config_header_id       IN NUMBER,
                                       pc_config_revision_number IN NUMBER)
    IS
      SELECT SUM(DECODE(aql.line_category_code, 'RETURN', -1, 1) *
                 NVL(aql.quantity, 0) *
                 NVL(aql.line_quote_price, 0))
        FROM ASO_QUOTE_LINES_ALL aql,
             ASO_QUOTE_LINE_DETAILS aqld
       WHERE aql.quote_line_id = aqld.quote_line_id
         AND aqld.config_header_id = pc_config_header_id
         AND aqld.config_revision_num = pc_config_revision_number
    ;

    l_config_header_id       ASO_QUOTE_LINE_DETAILS.config_header_id%TYPE;
    l_config_revision_number ASO_QUOTE_LINE_DETAILS.config_revision_num%TYPE;
    x_rollup_quote_price     NUMBER := 0;
  BEGIN
    OPEN C_Model_Config_Info(p_quote_line_id);
    FETCH C_Model_Config_Info INTO l_config_header_id, l_config_revision_number;
    CLOSE C_Model_Config_Info;

    OPEN C_Model_Rollup_Quote_Price (l_config_header_id,
                                     l_config_revision_number);
    FETCH C_Model_Rollup_Quote_Price INTO x_rollup_quote_price;
    CLOSE C_Model_Rollup_Quote_Price;

    RETURN x_rollup_quote_price;
  END Get_Model_Rollup_Quote_Price;

  /* Calculates the rollup cost for a configured model item */
  FUNCTION Get_Model_Rollup_Cost (p_quote_line_id NUMBER)
    RETURN NUMBER
  IS
    CURSOR C_Model_Config_Info (pc_quote_line_id IN NUMBER)
    IS
      SELECT config_header_id,
             config_revision_num
        FROM ASO_QUOTE_LINE_DETAILS
       WHERE quote_line_id = pc_quote_line_id
    ;

    /* declare a cursor to retrieve the rollup cost of a model item */
    CURSOR C_Model_Rollup_Cost (pc_config_header_id       IN NUMBER,
                                       pc_config_revision_number IN NUMBER)
    IS
      SELECT SUM(DECODE(aql.line_category_code, 'RETURN', -1, 1) *
                 NVL(aql.quantity, 0) *
                 NVL(aql.line_unit_cost, 0))
        FROM ASO_QUOTE_LINES_ALL aql,
             ASO_QUOTE_LINE_DETAILS aqld
       WHERE aql.quote_line_id = aqld.quote_line_id
         AND aqld.config_header_id = pc_config_header_id
         AND aqld.config_revision_num = pc_config_revision_number
    ;

    l_config_header_id       ASO_QUOTE_LINE_DETAILS.config_header_id%TYPE;
    l_config_revision_number ASO_QUOTE_LINE_DETAILS.config_revision_num%TYPE;
    x_rollup_cost     NUMBER := 0;
  BEGIN
    OPEN C_Model_Config_Info(p_quote_line_id);
    FETCH C_Model_Config_Info INTO l_config_header_id, l_config_revision_number;
    CLOSE C_Model_Config_Info;

    OPEN C_Model_Rollup_Cost (l_config_header_id,
                                     l_config_revision_number);
    FETCH C_Model_Rollup_Cost INTO x_rollup_cost;
    CLOSE C_Model_Rollup_Cost;

    RETURN x_rollup_cost;
  END Get_Model_Rollup_Cost;

  FUNCTION Get_Model_Margin_Percent (p_quote_line_id NUMBER)
      RETURN NUMBER
  IS
    l_rollup_cost     NUMBER := 0;
    l_rollup_price    NUMBER := 0;
    x_margin_percent  NUMBER := 0;
    l_compute_method     VARCHAR2(5):=NULL;
  BEGIN
   l_rollup_cost := Get_Model_Rollup_Cost (p_quote_line_id);
   l_rollup_price := Get_Model_Rollup_Quote_Price(p_quote_line_id);
   l_compute_method :=Oe_Sys_Parameters.Value('COMPUTE_MARGIN');
   IF l_compute_method = 'N'
   THEN
     RETURN NULL;
   END IF;

   IF l_compute_method = 'P'
   THEN
     IF l_rollup_price = 0
     THEN
       RETURN NULL;
     END IF;
     x_margin_percent :=(l_rollup_price-l_rollup_cost)/l_rollup_price*100;

    ELSIF l_compute_method = 'C'
    THEN
      IF l_rollup_cost = 0
      THEN
        RETURN NULL;
      END IF;
      x_margin_percent := (l_rollup_price-l_rollup_cost)/l_rollup_cost*100;
    END IF;
    RETURN x_margin_percent;
  EXCEPTION
  WHEN OTHERS THEN
     RETURN NULL;
  END Get_Model_Margin_Percent;

