  /* Determines whether a sorted expand list contains a expand quote line ID */
  FUNCTION In_Sorted_Expand_list (p_expand_id   NUMBER,
                                  p_expand_list JTF_NUMBER_TABLE)
    RETURN BOOLEAN
  IS
    l_expand_list JTF_NUMBER_TABLE := p_expand_list;
    l_index NUMBER;
  BEGIN
    IF p_expand_id IS NULL OR
       p_expand_list IS NULL
    THEN
      RETURN FALSE;
    END IF;

    l_index := CEIL((l_expand_list.FIRST + l_expand_list.LAST)/2);

    IF p_expand_id = l_expand_list(l_index)
    THEN
      RETURN TRUE;
    ELSIF p_expand_id > l_expand_list(l_expand_list.LAST) OR
          p_expand_id < l_expand_list(l_expand_list.FIRST) OR
          l_expand_list.COUNT = 1
    THEN
      RETURN FALSE;
    END IF;

    IF p_expand_id > l_expand_list(l_index)
    THEN
      l_expand_list.DELETE(l_expand_list.FIRST, l_index);
      RETURN In_Sorted_Expand_List(p_expand_id, l_expand_list);
    END IF;

    IF p_expand_id < l_expand_list(l_index)
    THEN
      l_expand_list.DELETE(l_index, l_expand_list.LAST);
      RETURN In_Sorted_Expand_List(p_expand_id, l_expand_list);
    END IF;

  END;

  /* Addes a quote line to a quote line array (collection) */
  PROCEDURE Add_Line (px_line_tbl IN OUT NOCOPY Line_Tabletype,
                      p_line_rec  IN     C_All_Lines_Info%ROWTYPE)
  IS
    l_index NUMBER := px_line_tbl.COUNT + 1;
    l_debug VARCHAR2(1);
  BEGIN
    l_debug := ASO_QUOTE_UTIL_PVT.is_debug_enabled;
    IF l_debug = 'Y' THEN
       ASO_QUOTE_UTIL_PVT.Enable_Debug_Pvt;
    END IF;

    px_line_tbl.EXTEND(1);
    px_line_tbl(l_index) := p_line_rec;

    IF l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.Debug('Added quote line '||TO_CHAR(p_line_rec.quote_line_id));
    END IF;
  END; -- END OF Add_Line


  /* Calculates the node type of a quote line */
  FUNCTION Get_Node_Type (p_quote_line_id IN NUMBER)
    RETURN NUMBER
  IS
    CURSOR C_Node_Type (pc_quote_line_id IN NUMBER)
    IS
      SELECT (1)
        FROM ASO_QUOTE_LINE_DETAILS
       WHERE ref_type_code = 'CONFIG'
         AND ref_line_id = pc_quote_line_id
         AND ROWNUM = 1
    ;
    x_node_type NUMBER := 0; -- default to terminal node
  BEGIN
    OPEN C_Node_Type(p_quote_line_id);
    FETCH C_Node_Type INTO x_node_type;
    CLOSE C_Node_Type;
    RETURN x_node_type;
  END; -- END OF Get_Node_Type

  /* Calculates the depth of a component item in a configuration */
  FUNCTION Get_Depth (p_quote_line_id IN NUMBER)
    RETURN NUMBER
  IS
    CURSOR C_Depth (pc_quote_line_id IN NUMBER)
    IS
      SELECT NVL(LENGTH(TRANSLATE(component_code, '-0123456789', '-')), 0) depth
        FROM ASO_QUOTE_LINE_DETAILS
       WHERE quote_line_id = pc_quote_line_id
    ;
    x_depth NUMBER := 0;
  BEGIN
    OPEN C_Depth(p_quote_line_id);
    FETCH C_Depth INTO x_depth;
    CLOSE C_Depth;
    RETURN x_depth;
  END;

  /* Determines whether a quote line is a component item in a configuration */
  FUNCTION Is_Component_Item (p_quote_line_id IN NUMBER)
    RETURN BOOLEAN
  IS
    CURSOR C_Component_Item (pc_quote_line_id IN NUMBER)
    IS
      SELECT item_type_code
        FROM ASO_QUOTE_LINES_ALL
       WHERE quote_line_id = pc_quote_line_id
    ;
    l_item_type_code VARCHAR2(10) := NULL;
  BEGIN
    OPEN C_Component_Item(p_quote_line_id);
    FETCH C_Component_Item INTO l_item_type_code;
    CLOSE C_Component_Item;
    IF (l_item_type_code = 'CFG')
    THEN RETURN TRUE;
    ELSE RETURN FALSE;
    END IF;
  END;

BEGIN

  l_debug := ASO_QUOTE_UTIL_PVT.Is_Debug_Enabled;
  IF l_debug = 'Y' THEN
    ASO_QUOTE_UTIL_PVT.Enable_Debug_Pvt;
    ASO_QUOTE_UTIL_PVT.Debug('ASO_QUOTE_PUB_W.Load_Lines_For_Display Begins');
  END IF;

  SAVEPOINT Load_Lines_For_Display_Pvt;

  /* initialize API return status to success */
  x_return_status := FND_API.G_RET_STS_SUCCESS;

  /* parameter processing */
  IF p_quote_header_id IS NULL OR
     p_quote_header_id = FND_API.G_MISS_NUM
  THEN
    x_return_status := FND_API.G_RET_STS_UNEXP_ERROR;
  END IF;

  /* validate quote header id */
  FOR quote_header_id_rec IN C_Validate_Quote_Header_Id(p_quote_header_id)
  LOOP
    IF quote_header_id_rec.quote_header_id IS NULL
    THEN x_return_status := FND_API.G_RET_STS_UNEXP_ERROR;
    END IF;
  END LOOP;

  /* reset Ui Line Number counters */
  ASO_LINE_NUM_INT.Reset_Line_Num;

  /* retrieve all line information for all lines in a quote, and populate data
     into l_line_tbl */
  IF l_debug = 'Y' THEN
    ASO_QUOTE_UTIL_PVT.Debug('MAJOR LOOP: all line records in quote '||TO_CHAR(p_quote_header_id));
  END IF;

  FOR all_lines_info_rec IN C_All_Lines_Info(p_quote_header_id)
  LOOP

    IF l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.Debug('quote_line_id = '||TO_CHAR(all_lines_info_rec.quote_line_id));
    END IF;

    /**
       now we are to add the following quote lines into l_line_tbl:
       1. top-level quote lines, including (these are not in the expand list):
          (1) standard line
          (2) model line
          (3) service lines associated with a pending order or an installbase item
       2. quote lines whose quote_line_id are in the expand list (p_expand_quote_line_id)
       3. quote lines whose parents are in the expand list (p_expand_quote_line_id)
       4. services associated with the above quote lines
    **/
    IF NVL(all_lines_info_rec.service_ref_type_code, 'X') <> 'QUOTE' AND
       NVL(all_lines_info_rec.ref_type_code, 'X') <> 'CONFIG'
    THEN
      /* this quote line is a standard line or a service line associated with
         a pending order or an installbase item */
      all_lines_info_rec.node_type := TERMINAL_NODE;
      Add_Line(l_line_tbl, all_lines_info_rec);

    ELSIF all_lines_info_rec.item_type_code = 'MDL'
    THEN
      /* this quote line is a model line */
      all_lines_info_rec.node_type := Get_Node_Type(all_lines_info_rec.quote_line_id);
      Add_Line(l_line_tbl, all_lines_info_rec);

    ELSIF all_lines_info_rec.service_ref_type_code = 'QUOTE' AND
          NOT Is_Component_Item(all_lines_info_rec.service_ref_line_id)
    THEN
      /* this quote line is a service associated to a top-level line (standard
         line or a model line */
      all_lines_info_rec.node_type := TERMINAL_NODE;
      IF In_Sorted_Expand_List(all_lines_info_rec.service_ref_line_id, p_expand_quote_line_id)
      THEN
        /* this quote line is a service associated to a configured model line
           to be expanded */
        all_lines_info_rec.depth := 1;
      ELSE
        all_lines_info_rec.depth := 0;
      END IF;
      Add_Line(l_line_tbl, all_lines_info_rec);

    ELSIF p_expand_quote_line_id IS NOT NULL
    THEN
      /* now the quote line is one of the following:
         1. a component item
         2. services associated with a component item */
      IF In_Sorted_Expand_List(all_lines_info_rec.quote_line_id, p_expand_quote_line_id)
      THEN
        /* this quote line is a component item to be expanded */
        all_lines_info_rec.node_type := INTERMEDIATE_NODE;
        Add_Line(l_line_tbl, all_lines_info_rec);
      ELSE
        /* this quote line is not in the expand list, thus it could be:
           1. a component item whose parent is in the expand list
           2. a service item whose parent is in the expand list
           3. a service item associated to a component item whose parent is in the expand
              list. */
        IF In_Sorted_Expand_List(all_lines_info_rec.ref_line_id, p_expand_quote_line_id)
           OR
           In_Sorted_Expand_List(all_lines_info_rec.service_ref_line_id, p_expand_quote_line_id)
        THEN
          /* this quote line is one of the following:
             1. a component item whose parent is in the expand list
             2. a service item whose parent is in the expand list */
          IF all_lines_info_rec.service_ref_type_code = 'QUOTE'
          THEN -- a service
            all_lines_info_rec.node_type := TERMINAL_NODE;
            all_lines_info_rec.depth := Get_Depth(all_lines_info_rec.service_ref_line_id) + 1;
          ELSIF all_lines_info_rec.ref_type_code = 'CONFIG'
          THEN -- a component item
            all_lines_info_rec.node_type := Get_Node_Type(all_lines_info_rec.quote_line_id);
            all_lines_info_rec.depth := Get_Depth(all_lines_info_rec.ref_line_id) + 1;
          END IF;
          Add_Line(l_line_tbl, all_lines_info_rec);
        ELSE
          IF all_lines_info_rec.service_ref_line_id = l_line_tbl(l_line_tbl.LAST).quote_line_id
             OR
             all_lines_info_rec.service_ref_line_id = l_line_tbl(l_line_tbl.LAST).service_ref_line_id
          THEN
            /* this quote line is a service item associated to a component item
               whose parent is in the expand list */
            all_lines_info_rec.node_type := TERMINAL_NODE;
            /* if the service's parent is a terminal component item, then the depth of the
               should be the depth of the component item + 1 otherwise, should be the same */
            IF Get_Node_Type(all_lines_info_rec.service_ref_line_id) = TERMINAL_NODE
            THEN
              all_lines_info_rec.depth := Get_Depth(all_lines_info_rec.service_ref_line_id) + 1;
            ELSE
              all_lines_info_rec.depth := Get_Depth(all_lines_info_rec.service_ref_line_id);
            END IF;
            Add_Line(l_line_tbl, all_lines_info_rec);
          END IF;
        END IF;
      END IF;
    END IF;
  END LOOP; -- END OF LOOP all line records in a quote

  IF l_debug = 'Y' THEN
    ASO_QUOTE_UTIL_PVT.Debug('END OF Major Loop...');
  END IF;

  /*
     now we have filtered out unwanted quote line records. loop through l_line_tbl,
     do the following:
     (1) populate output tables prefixed with 'x_ql_', 'x_qld_'
     (2) if p_load_ln_man_prc_adj_flag is 'Y', calculate line-level manual price
         adjusted amount, and populate output table prefixed with 'x_qlp_'
     (3) if p_load_ln_tax_flag is 'Y', calculate line-level tax information,
         and populate output tables prefixed with 'x_qlt_'
  */
  IF l_line_tbl.COUNT > 0
  THEN
    x_ql_quote_line_id := JTF_NUMBER_TABLE();
    x_ql_inventory_item_id := JTF_NUMBER_TABLE();
    x_ql_organization_id := JTF_NUMBER_TABLE();
    x_ql_ui_line_number := JTF_VARCHAR2_TABLE_1000();
    x_ql_line_number := JTF_NUMBER_TABLE();
    x_ql_item_type_code := JTF_VARCHAR2_TABLE_100();
    x_ql_quantity := JTF_NUMBER_TABLE();
    x_ql_uom_code := JTF_VARCHAR2_TABLE_100();
    x_ql_line_list_price := JTF_NUMBER_TABLE();
    x_ql_line_quote_price := JTF_NUMBER_TABLE();
    x_ql_line_adjusted_amount := JTF_NUMBER_TABLE();
    x_ql_display_arithmetic_op := JTF_VARCHAR2_TABLE_100();
    x_ql_line_category_code	:= JTF_VARCHAR2_TABLE_100();
    x_ql_order_line_type_id	:= JTF_NUMBER_TABLE();
    x_ql_depth := JTF_NUMBER_TABLE();
    x_ql_node_type := JTF_NUMBER_TABLE();
    x_ql_rollup_quote_price	:= JTF_NUMBER_TABLE();
    x_ql_ship_quote_price := JTF_NUMBER_TABLE();
    x_ql_serviceable_product_flag := JTF_VARCHAR2_TABLE_100();
    x_ql_service_item_flag := JTF_VARCHAR2_TABLE_100();
    x_ql_description := JTF_VARCHAR2_TABLE_300();
    x_ql_concatenated_segments := JTF_VARCHAR2_TABLE_100();
    x_ql_line_adjusted_percent := JTF_NUMBER_TABLE();
    x_ql_line_type_source_flag := JTF_VARCHAR2_TABLE_100();
    x_ql_start_date_active := JTF_DATE_TABLE();
    x_ql_prc_ln_type_indicator := JTF_VARCHAR2_TABLE_100();
    x_qld_config_delta := JTF_NUMBER_TABLE();
    x_qld_instance_id := JTF_NUMBER_TABLE();
    x_qld_ref_line_id := JTF_NUMBER_TABLE();
    x_qld_ref_type_code := JTF_VARCHAR2_TABLE_100();
    x_qld_service_ref_line_id := JTF_NUMBER_TABLE();
    x_qld_service_ref_type_code := JTF_VARCHAR2_TABLE_100();
    x_qld_config_instance_name := JTF_VARCHAR2_TABLE_300();
    x_qld_complete_config_flag := JTF_VARCHAR2_TABLE_100();

    x_ql_quote_line_id.EXTEND(l_line_tbl.COUNT);
    x_ql_inventory_item_id.EXTEND(l_line_tbl.COUNT);
    x_ql_organization_id.EXTEND(l_line_tbl.COUNT);
    x_ql_ui_line_number.EXTEND(l_line_tbl.COUNT);
    x_ql_line_number.EXTEND(l_line_tbl.COUNT);
    x_ql_item_type_code.EXTEND(l_line_tbl.COUNT);
    x_ql_quantity.EXTEND(l_line_tbl.COUNT);
    x_ql_uom_code.EXTEND(l_line_tbl.COUNT);
    x_ql_line_list_price.EXTEND(l_line_tbl.COUNT);
    x_ql_line_quote_price.EXTEND(l_line_tbl.COUNT);
    x_ql_line_adjusted_amount.EXTEND(l_line_tbl.COUNT);
    x_ql_display_arithmetic_op.EXTEND(l_line_tbl.COUNT);
    x_ql_line_category_code.EXTEND(l_line_tbl.COUNT);
    x_ql_order_line_type_id.EXTEND(l_line_tbl.COUNT);
    x_ql_depth.EXTEND(l_line_tbl.COUNT);
    x_ql_node_type.EXTEND(l_line_tbl.COUNT);
    x_ql_rollup_quote_price.EXTEND(l_line_tbl.COUNT);
    x_ql_ship_quote_price.EXTEND(l_line_tbl.COUNT);
    x_ql_serviceable_product_flag.EXTEND(l_line_tbl.COUNT);
    x_ql_service_item_flag.EXTEND(l_line_tbl.COUNT);
    x_ql_description.EXTEND(l_line_tbl.COUNT);
    x_ql_concatenated_segments.EXTEND(l_line_tbl.COUNT);
    x_ql_line_adjusted_percent.EXTEND(l_line_tbl.COUNT);
    x_ql_line_type_source_flag.EXTEND(l_line_tbl.COUNT);
    x_ql_start_date_active.EXTEND(l_line_tbl.COUNT);
    x_ql_prc_ln_type_indicator.EXTEND(l_line_tbl.COUNT);
    x_qld_config_delta.EXTEND(l_line_tbl.COUNT);
    x_qld_instance_id.EXTEND(l_line_tbl.COUNT);
    x_qld_ref_line_id.EXTEND(l_line_tbl.COUNT);
    x_qld_ref_type_code.EXTEND(l_line_tbl.COUNT);
    x_qld_service_ref_line_id.EXTEND(l_line_tbl.COUNT);
    x_qld_service_ref_type_code.EXTEND(l_line_tbl.COUNT);
    x_qld_config_instance_name.EXTEND(l_line_tbl.COUNT);
    x_qld_complete_config_flag.EXTEND(l_line_tbl.COUNT);

    /* now populate output tables begin with x_ql_ and x_qld_ */
    FOR i IN 1..l_line_tbl.COUNT
    LOOP
      x_ql_quote_line_id(i) := l_line_tbl(i).quote_line_id;
      x_ql_inventory_item_id(i) := l_line_tbl(i).inventory_item_id;
      x_ql_organization_id(i) := l_line_tbl(i).organization_id;
      x_ql_ui_line_number(i) := l_line_tbl(i).ui_line_number;
      x_ql_line_number(i) := l_line_tbl(i).line_number;
      x_ql_item_type_code(i) := l_line_tbl(i).item_type_code;
      x_ql_quantity(i) := l_line_tbl(i).quantity;
      x_ql_uom_code(i) := l_line_tbl(i).uom_code;
      x_ql_line_list_price(i) := l_line_tbl(i).line_list_price;
      x_ql_line_quote_price(i) := l_line_tbl(i).line_quote_price;
      x_ql_line_adjusted_amount(i) := l_line_tbl(i).line_adjusted_amount;
      x_ql_display_arithmetic_op(i) := l_line_tbl(i).display_arithmetic_operator;
      x_ql_line_category_code(i) := l_line_tbl(i).line_category_code;
      x_ql_order_line_type_id(i) := l_line_tbl(i).order_line_type_id;
      x_ql_depth(i) := l_line_tbl(i).depth;
      x_ql_node_type(i) := l_line_tbl(i).node_type;

      IF x_ql_item_type_code(i) = 'MDL' AND x_ql_node_type(i) = INTERMEDIATE_NODE
      THEN
        /* this line is a configured model line.  we need to calculate the
           rollup quote price */
        x_ql_rollup_quote_price(i) := Get_Model_Rollup_Quote_Price(x_ql_quote_line_id(i));
      END IF;

      x_ql_ship_quote_price(i) := l_line_tbl(i).ship_quote_price;
      x_ql_serviceable_product_flag(i) := l_line_tbl(i).serviceable_product_flag;
      x_ql_service_item_flag(i) := l_line_tbl(i).service_item_flag;
      x_ql_description(i) := l_line_tbl(i).description;
      x_ql_concatenated_segments(i) := l_line_tbl(i).concatenated_segments;
      x_ql_line_adjusted_percent(i) := l_line_tbl(i).line_adjusted_percent;
      x_ql_line_type_source_flag(i) := l_line_tbl(i).line_type_source_flag;
      x_ql_start_date_active(i) := l_line_tbl(i).start_date_active;
      x_ql_prc_ln_type_indicator(i) := l_line_tbl(i).pricing_line_type_indicator;
      x_qld_config_delta(i) := l_line_tbl(i).config_delta;
      x_qld_instance_id(i) := l_line_tbl(i).instance_id;
      x_qld_ref_line_id(i) := l_line_tbl(i).ref_line_id;
      x_qld_ref_type_code(i) := l_line_tbl(i).ref_type_code;
      x_qld_service_ref_line_id(i) := l_line_tbl(i).service_ref_line_id;
      x_qld_service_ref_type_code(i) := l_line_tbl(i).service_ref_type_code;
      x_qld_config_instance_name(i) := l_line_tbl(i).config_instance_name;
      x_qld_complete_config_flag(i) := l_line_tbl(i).complete_configuration_flag;
    END LOOP;

    /* now load line-level manual price adjusted amount and/or
       line-level tax according to the flags */
    IF p_load_ln_man_prc_adj_flag = 'Y' OR
       p_load_ln_tax_flag = 'Y'
    THEN
      IF p_load_ln_man_prc_adj_flag = 'Y'
      THEN
        x_qlp_total_man_prc_adj_amt := JTF_NUMBER_TABLE();
        x_qlp_total_man_prc_adj_amt.EXTEND(l_line_tbl.COUNT);
      END IF;

      FOR j IN 1..l_line_tbl.COUNT
      LOOP
        IF p_load_ln_man_prc_adj_flag = 'Y'
        THEN
          IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.Debug('Calculating total line-level manual price adjusted amount for line ' || l_line_tbl(j).quote_line_id);
          END IF;
          OPEN C_Ttl_Line_Manu_Prc_Adj_Amt(l_line_tbl(j).quote_line_id);
          FETCH C_Ttl_Line_Manu_Prc_Adj_Amt INTO x_qlp_total_man_prc_adj_amt(j);
          CLOSE C_Ttl_Line_Manu_Prc_Adj_Amt;
        END IF;

        IF p_load_ln_tax_flag = 'Y'
        THEN
          IF l_debug = 'Y' THEN
            ASO_QUOTE_UTIL_PVT.Debug('Retrieving line-level tax details for line ' || l_line_tbl(j).quote_line_id);
          END IF;
          IF x_qlt_tax_quote_line_id IS NULL
          THEN x_qlt_tax_quote_line_id := JTF_NUMBER_TABLE();
          END IF;
          IF x_qlt_tax_code IS NULL
          THEN x_qlt_tax_code := JTF_VARCHAR2_TABLE_100();
          END IF;
          IF x_qlt_tax_rate IS NULL
          THEN x_qlt_tax_rate := JTF_NUMBER_TABLE();
          END IF;
          IF x_qlt_tax_amount IS NULL
          THEN x_qlt_tax_amount := JTF_NUMBER_TABLE();
          END IF;
          IF x_qlt_tax_inclusive_flag IS NULL
          THEN x_qlt_tax_inclusive_flag := JTF_VARCHAR2_TABLE_100();
          END IF;
          l_tbl_index := x_qlt_tax_quote_line_id.COUNT;

          FOR line_tax_rec IN C_Line_Tax_Details(l_line_tbl(j).quote_line_id)
          LOOP
            x_qlt_tax_quote_line_id.EXTEND();
            x_qlt_tax_code.EXTEND();
            x_qlt_tax_rate.EXTEND();
            x_qlt_tax_amount.EXTEND();
            x_qlt_tax_inclusive_flag.EXTEND();
            l_tbl_index := l_tbl_index + 1;
            x_qlt_tax_quote_line_id(l_tbl_index) := line_tax_rec.tax_quote_line_id;
            x_qlt_tax_code(l_tbl_index) := line_tax_rec.tax_code;
            x_qlt_tax_rate(l_tbl_index) := line_tax_rec.tax_rate;
            x_qlt_tax_amount(l_tbl_index) := line_tax_rec.tax_amount;
            x_qlt_tax_inclusive_flag(l_tbl_index) := line_tax_rec.tax_inclusive_flag;
          END LOOP;
        END IF;
      END LOOP; -- END OF LOOP l_line_tbl
    END IF; -- END OF IF p_load_ln_man_prc_adj_flag = 'Y' OR p_load_ln_tax_flag = 'Y'
  END IF; -- END IF l_line_tbl.COUNT > 0

  /*
    retrieve header-level information required for OUT NOCOPYput based on the input flags,
    including:
    (1) header-level tax information, populate into output tables prefixed with 'x_qt_'
    (2) header-level manual, applied, non-freight price adjusted operand, populate into
        output tables prefixed with 'x_qp_'
  */

  IF p_load_hdr_tax_flag = 'Y'
  THEN
    IF l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.Debug('Retrieving total header-level tax details...');
    END IF;
    x_qt_tax_code := JTF_VARCHAR2_TABLE_100();
    x_qt_tax_amount := JTF_NUMBER_TABLE();
    x_qt_tax_inclusive_flag := JTF_VARCHAR2_TABLE_100();
    l_tbl_index := 0;
    FOR header_tax_rec IN C_Header_Tax_Details(p_quote_header_id)
    LOOP
      x_qt_tax_code.EXTEND;
      x_qt_tax_amount.EXTEND;
      x_qt_tax_inclusive_flag.EXTEND;
      l_tbl_index := l_tbl_index + 1;
      x_qt_tax_code(l_tbl_index) := header_tax_rec.tax_code;
      x_qt_tax_amount(l_tbl_index) := header_tax_rec.tax_amount;
      x_qt_tax_inclusive_flag(l_tbl_index) := header_tax_rec.tax_inclusive_flag;
    END LOOP;
  END IF;

  IF p_load_hdr_man_prc_adj_flag = 'Y'
  THEN
    IF l_debug = 'Y' THEN
      ASO_QUOTE_UTIL_PVT.Debug('Calculating total header-level manual price adjusted operand...');
    END IF;
    OPEN C_Ttl_Hdr_Manu_Prc_Adj_Op(p_quote_header_id);
    FETCH C_Ttl_Hdr_Manu_Prc_Adj_Op INTO x_qp_total_man_prc_adj_op;
    CLOSE C_Ttl_Hdr_Manu_Prc_Adj_Op;
  END IF;

  -- standard call to get message count and if count is 1, get the message info
  FND_MSG_PUB.Count_And_Get(
    p_encoded => FND_API.G_FALSE,
    p_count   => x_msg_count,
    p_data    => x_msg_data);

  IF l_debug = 'Y' THEN
    ASO_QUOTE_UTIL_PVT.debug('ASO_QUOTE_PUB_W.Load_Lines_For_Display Ends');
    ASO_QUOTE_UTIL_PVT.Disable_Debug_Pvt;
  END IF;

EXCEPTION

  WHEN OTHERS THEN
    ASO_UTILITY_PVT.HANDLE_EXCEPTIONS(
    P_API_NAME => l_api_name
   ,P_PKG_NAME => 'ASO_QUOTE_PUB_W'
   ,P_EXCEPTION_LEVEL => ASO_UTILITY_PVT.G_EXC_OTHERS
   ,P_PACKAGE_TYPE => ASO_UTILITY_PVT.G_PUB
   ,X_MSG_COUNT => X_MSG_COUNT
   ,X_MSG_DATA => X_MSG_DATA
   ,X_RETURN_STATUS => X_RETURN_STATUS);

   ASO_QUOTE_UTIL_PVT.Disable_Debug_Pvt;
   ROLLBACK TO Load_Lines_For_Display_Pvt;

END;

