	x_sequence_id          OUT NOCOPY JTF_NUMBER_TABLE,
	x_error_record_type    OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_title                OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_article_id           OUT NOCOPY JTF_NUMBER_TABLE,
	x_deliverable_id       OUT NOCOPY JTF_NUMBER_TABLE,
	x_section_name         OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_error_severity       OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_QA_code              OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_message_name         OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_problem_short_desc   OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_problem_details      OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_suggestion           OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_creation_date        OUT NOCOPY JTF_DATE_TABLE,
	x_reference_column1    OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_reference_column2    OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_reference_column3    OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_reference_column4    OUT NOCOPY JTF_VARCHAR2_TABLE_300,
	x_reference_column5    OUT NOCOPY JTF_VARCHAR2_TABLE_300,
    x_error_record_type_name OUT NOCOPY JTF_VARCHAR2_TABLE_2000,
    x_error_severity_name    OUT NOCOPY JTF_VARCHAR2_TABLE_2000,
	x_qa_return_status     OUT NOCOPY VARCHAR2,
	x_return_status        OUT NOCOPY VARCHAR2,
	x_msg_data             OUT NOCOPY VARCHAR2,
	x_msg_count 	   	   OUT NOCOPY NUMBER,
	x_record_count 	   	   OUT NOCOPY NUMBER
)
AS
	l_api_name         CONSTANT VARCHAR2(30)   := 'QA_check_for_contract';
	l_debug                     VARCHAR2(1);
	i NUMBER ;
	lx_qa_result_tbl     OKC_TERMS_QA_GRP.qa_result_tbl_type;
BEGIN

	l_debug := ASO_QUOTE_UTIL_PVT.is_debug_enabled;

	IF l_debug = 'Y' THEN
	   ASO_QUOTE_UTIL_PVT.Debug('Begin calling OKC_TERMS_QA_GRP.QA_doc');
	END IF;

	OKC_TERMS_QA_GRP.QA_doc
	(
		p_api_version           => 1.0,
		p_init_msg_list     	=> p_init_msg_list,
		p_qa_mode               => 'NORMAL',
		p_doc_type              => 'QUOTE',
		p_doc_id                => p_quote_header_id,
		x_qa_result_tbl         => lx_qa_result_tbl,
		x_qa_return_status      => x_qa_return_status,
		x_return_status         => x_return_status,
		x_msg_data              => x_msg_data,
		x_msg_count             => x_msg_count,
		p_qa_terms_only         => 'Y'
	);

	FND_MSG_PUB.Count_And_Get
	(
		p_encoded => FND_API.G_FALSE,
		p_count => x_msg_count,
		p_data  => x_msg_data
	);

	IF l_debug = 'Y' THEN
		ASO_QUOTE_UTIL_PVT.Debug('Returning from OKC_TERMS_QA_GRP.QA_doc');
		ASO_QUOTE_UTIL_PVT.Disable_Debug_Pvt;
	END IF;

	IF x_return_status = FND_API.G_RET_STS_ERROR THEN
	   RAISE FND_API.G_EXC_ERROR;
	END IF;
	IF x_return_status = FND_API.G_RET_STS_UNEXP_ERROR THEN
		RAISE FND_API.G_EXC_UNEXPECTED_ERROR;
	END IF;

	x_record_count := lx_qa_result_tbl.COUNT;
	If x_record_count > 0 THEN
	   x_sequence_id := JTF_NUMBER_TABLE();
	   x_error_record_type := JTF_VARCHAR2_TABLE_300();
	   x_article_id := JTF_NUMBER_TABLE();
	   x_deliverable_id := JTF_NUMBER_TABLE();
	   x_title := JTF_VARCHAR2_TABLE_300();
	   x_section_name := JTF_VARCHAR2_TABLE_300();
	   x_error_severity := JTF_VARCHAR2_TABLE_300();
	   x_qa_code := JTF_VARCHAR2_TABLE_300();
	   x_message_name := JTF_VARCHAR2_TABLE_300();
	   x_problem_short_desc := JTF_VARCHAR2_TABLE_300();
	   x_problem_details := JTF_VARCHAR2_TABLE_300();
	   x_suggestion := JTF_VARCHAR2_TABLE_300();
	   x_reference_column1 := JTF_VARCHAR2_TABLE_300();
	   x_reference_column2 := JTF_VARCHAR2_TABLE_300();
	   x_reference_column3 := JTF_VARCHAR2_TABLE_300();
	   x_reference_column4 := JTF_VARCHAR2_TABLE_300();
	   x_reference_column5 := JTF_VARCHAR2_TABLE_300();
       x_error_record_type_name := JTF_VARCHAR2_TABLE_2000();
       x_error_severity_name := JTF_VARCHAR2_TABLE_2000();
	   x_creation_date := JTF_DATE_TABLE();

	   x_sequence_id.EXTEND(x_record_count);
	   x_error_record_type.EXTEND(x_record_count);
	   x_deliverable_id.EXTEND(x_record_count);
	   x_title.EXTEND(x_record_count);
	   x_article_id.EXTEND(x_record_count);
	   x_section_name.EXTEND(x_record_count);
	   x_error_severity.EXTEND(x_record_count);
	   x_qa_code.EXTEND(x_record_count);
	   x_message_name.EXTEND(x_record_count);
	   x_problem_short_desc.EXTEND(x_record_count);
	   x_problem_details.EXTEND(x_record_count);
	   x_suggestion.EXTEND(x_record_count);
	   x_reference_column1.EXTEND(x_record_count);
	   x_reference_column2.EXTEND(x_record_count);
	   x_reference_column3.EXTEND(x_record_count);
	   x_reference_column4.EXTEND(x_record_count);
	   x_reference_column5.EXTEND(x_record_count);
	   x_error_record_type_name.EXTEND(x_record_count);
	   x_error_severity_name.EXTEND(x_record_count);
	   x_creation_date.EXTEND(x_record_count);

	   FOR i IN lx_qa_result_tbl.FIRST..lx_qa_result_tbl.LAST LOOP
   	  			x_sequence_id(i)      	:= lx_qa_result_tbl(i).sequence_id;
   	  			x_error_record_type(i)      	:= lx_qa_result_tbl(i).error_record_type;
   	  			x_title(i)      	:= lx_qa_result_tbl(i).title;
   	  			x_article_id(i)      	:= lx_qa_result_tbl(i).article_id;
   	  			x_deliverable_id(i)      	:= lx_qa_result_tbl(i).deliverable_id;
   	  			x_section_name(i)      	:= lx_qa_result_tbl(i).section_name;
   	  			x_error_severity(i)      	:= lx_qa_result_tbl(i).error_severity;
   	  			x_qa_code(i)      	:= lx_qa_result_tbl(i).qa_code;
   	  			x_message_name(i)      	:= lx_qa_result_tbl(i).message_name;
   	  			x_problem_short_desc(i)      	:= lx_qa_result_tbl(i).problem_short_desc;
   	  			x_problem_details(i)      	:= lx_qa_result_tbl(i).problem_details;
   	  			x_suggestion(i)      	:= lx_qa_result_tbl(i).suggestion;
   	  			x_creation_date(i)      	:= lx_qa_result_tbl(i).creation_date;
   	  			x_reference_column1(i)      	:= lx_qa_result_tbl(i).reference_column1;
   	  			x_reference_column2(i)      	:= lx_qa_result_tbl(i).reference_column2;
   	  			x_reference_column3(i)      	:= lx_qa_result_tbl(i).reference_column3;
   	  			x_reference_column4(i)      	:= lx_qa_result_tbl(i).reference_column4;
   	  			x_reference_column5(i)      	:= lx_qa_result_tbl(i).reference_column5;
   	  			x_error_record_type_name(i)   	:= lx_qa_result_tbl(i).error_record_type_name;
   	  			x_error_severity_name(i)      	:= lx_qa_result_tbl(i).error_severity_name;
		END LOOP;
	END IF;


   EXCEPTION
   WHEN FND_API.G_EXC_ERROR
   THEN
    x_return_status := FND_API.G_RET_STS_ERROR;
    FND_MSG_PUB.Count_And_Get(
      p_encoded => FND_API.G_FALSE,
      p_count   => x_msg_count,
      p_data    => x_msg_data
    );
    IF l_debug = 'Y' THEN
       ASO_QUOTE_UTIL_PVT.Disable_Debug_Pvt;
    END IF;

   WHEN FND_API.G_EXC_UNEXPECTED_ERROR
   THEN
    x_return_status := FND_API.G_RET_STS_UNEXP_ERROR;
    FND_MSG_PUB.Count_And_Get(
      p_encoded => FND_API.G_FALSE,
      p_count   => x_msg_count,
      p_data    => x_msg_data
    );
    IF l_debug = 'Y' THEN
       ASO_QUOTE_UTIL_PVT.Disable_Debug_Pvt;
    END IF;

   WHEN OTHERS THEN
    x_return_status := FND_API.G_RET_STS_UNEXP_ERROR;
    IF FND_MSG_PUB.Check_Msg_Level(FND_MSG_PUB.G_MSG_LVL_UNEXP_ERROR)
    THEN
      FND_MSG_PUB.Add_Exc_Msg(
        G_PKG_NAME,
        l_api_name
      );
    END IF;
    FND_MSG_PUB.Count_And_Get(
      p_encoded => FND_API.G_FALSE,
      p_count   => x_msg_count,
      p_data    => x_msg_data
    );
    IF l_debug = 'Y' THEN
       ASO_QUOTE_UTIL_PVT.Disable_Debug_Pvt;
    END IF;
END QA_check_for_contract;

