procedure init_table_data_in_cer(p_copy_entity_txn_id  in number) is
  --
  cursor c_table_data_in_cer is
   select tr.table_alias, tr.table_route_id
   from pqh_table_route tr
   where  tr.table_route_id in(
    select table_route_id from pqh_copy_entity_results cer
    where cer.copy_entity_txn_id = p_copy_entity_txn_id);
  --
begin
  --
  ben_pd_copy_to_ben_one.g_table_data_in_cer.delete;
  ben_pd_copy_to_ben_one.g_table_data_in_cer(0)    := null ;
  ben_pd_copy_to_ben_one.g_table_data_in_cer_count := 0 ;
  --
  for l_table_data_in_cer in c_table_data_in_cer loop
    --
    ben_pd_copy_to_ben_one.g_table_data_in_cer(ben_pd_copy_to_ben_one.g_table_data_in_cer_count).table_alias
              := l_table_data_in_cer.table_alias;
    ben_pd_copy_to_ben_one.g_table_data_in_cer(ben_pd_copy_to_ben_one.g_table_data_in_cer_count).table_route_id
              := l_table_data_in_cer.table_route_id;
    ben_pd_copy_to_ben_one.g_table_data_in_cer_count    := ben_pd_copy_to_ben_one.g_table_data_in_cer_count + 1;
    --
  end loop;
  --
end init_table_data_in_cer;
