  WHEN xla_exceptions_pkg.application_exception THEN
      RAISE;
  WHEN OTHERS THEN
       xla_exceptions_pkg.raise_message
           (p_location => 'XLA_00200_AAD_S_000012_BC_PKG.AcctLineType_17');
END AcctLineType_17;
