        , p_component_appl_id       => l_component_appl_id
        , p_amb_context_code        => l_amb_context_code
        , p_entity_code             => 'AP_INVOICES'
        , p_event_class_code        => 'DEBIT MEMOS'
        , p_side                    => 'NA'
        );

