 , p_source_type_code      => 'Y'
 , p_source_application_id =>  602
);
   l_rec_acct_attrs.array_acct_attr_code(3) := 'ALLOC_TO_DISTRIBUTION_TYPE';
   l_rec_acct_attrs.array_char_value(3)  := p_source_17;
