   l_rec_acct_attrs.array_char_value(36)  := p_source_45;
   l_rec_acct_attrs.array_acct_attr_code(37) := 'REVERSED_DISTRIBUTION_ID1';
   l_rec_acct_attrs.array_num_value(37)  :=  to_char(p_source_46);
   l_rec_acct_attrs.array_acct_attr_code(38) := 'REVERSED_DISTRIBUTION_TYPE';
   l_rec_acct_attrs.array_char_value(38)  := p_source_17;
   l_rec_acct_attrs.array_acct_attr_code(39) := 'STATISTICAL_AMOUNT';
   l_rec_acct_attrs.array_num_value(39)  := p_source_47;
