') =  'IPV' AND 
p_source_10 <  0) OR 
NVL(p_source_8,'
') =  'ACCRUAL') AND 
NVL(p_source_5,'
') =  'Unexpired' AND 
NVL(p_source_11,'
') <>  'Y' AND 
NVL(p_source_9,'
') <>  'Y'
 THEN 

   --
