--
INSERT INTO xla_diag_sources  --line2
(
        event_id
      , ledger_id
      , sla_ledger_id
      , description_language
      , object_name
      , object_type_code
      , line_number
      , source_application_id
      , source_type_code
      , source_code
      , source_value
      , source_meaning
      , created_by
      , creation_date
      , last_update_date
      , last_updated_by
      , last_update_login
      , program_update_date
      , program_application_id
      , program_id
      , request_id
)
SELECT  event_id
      , p_target_ledger_id
      , p_sla_ledger_id
      , p_language
      , object_name
      , object_type_code
      , line_number
      , source_application_id
      , source_type_code
      , source_code
      , SUBSTR(source_value,1,1996)
      , SUBSTR(source_meaning ,1,200)
      , xla_environment_pkg.g_Usr_Id
      , TRUNC(SYSDATE)
      , TRUNC(SYSDATE)
      , xla_environment_pkg.g_Usr_Id
      , xla_environment_pkg.g_Login_Id
      , TRUNC(SYSDATE)
      , xla_environment_pkg.g_Prog_Appl_Id
      , xla_environment_pkg.g_Prog_Id
      , xla_environment_pkg.g_Req_Id
  FROM (
       SELECT xet.event_id                  event_id
            , l1.line_number                 line_number
            , CASE r
               WHEN 1 THEN 'AP_EXTRACT_INVOICE_DTLS_BC_V' 
                WHEN 2 THEN 'AP_EXTRACT_INVOICE_DTLS_BC_V' 
                WHEN 3 THEN 'FV_XLA_AP_REF_V' 
                WHEN 4 THEN 'FV_XLA_AP_REF_V' 
                WHEN 5 THEN 'FV_XLA_AP_REF_V' 
                WHEN 6 THEN 'FV_XLA_AP_REF_V' 
