--
CURSOR header_cur
IS
SELECT /*+ leading(xet) cardinality(xet,1) */
-- Event Class Code: CREDIT MEMOS
    xet.entity_id
   ,xet.legal_entity_id
   ,xet.entity_code
   ,xet.transaction_number
   ,xet.event_id
   ,xet.event_class_code
   ,xet.event_type_code
   ,xet.event_number
   ,xet.event_date
   ,xet.transaction_date
   ,xet.reference_num_1
   ,xet.reference_num_2
   ,xet.reference_num_3
   ,xet.reference_num_4
   ,xet.reference_char_1
   ,xet.reference_char_2
   ,xet.reference_char_3
   ,xet.reference_char_4
   ,xet.reference_date_1
   ,xet.reference_date_2
   ,xet.reference_date_3
   ,xet.reference_date_4
   ,xet.event_created_by
   ,xet.budgetary_control_flag 
  , h2.AI_PAYMENT_STATUS_FLAG    source_7
  , h2.AI_INVOICE_TYPE_LOOKUP_CODE    source_13
  , fvl13.meaning   source_13_meaning
  , h2.AI_INVOICE_ID    source_20
  , h2.AI_INVOICE_CURRENCY_CODE    source_29
  , h2.INV_EXCHANGE_DATE    source_36
  , h2.INV_EXCHANGE_RATE    source_37
  , h2.INV_EXCHANGE_RATE_TYPE    source_38
  , h2.AI_VENDOR_ID    source_43
  , h2.AI_VENDOR_SITE_ID    source_44
  , h2.THIRD_PARTY_TYPE    source_45
  , h2.INV_DOC_SEQUENCE_CATEGORY    source_71
  , h2.INV_DOC_SEQUENCE_IDENTIFIER    source_72
  , h2.INV_DOC_SEQUENCE_VALUE    source_73
  FROM xla_events_gt     xet 
  , AP_INVOICE_EXTRACT_HEADER_V  h2
  , fnd_lookup_values    fvl13
 WHERE xet.event_date between p_pad_start_date and p_pad_end_date
   and xet.event_class_code = C_EVENT_CLASS_CODE
   and xet.event_status_code <> 'N'  AND h2.event_id = xet.event_id
   AND fvl13.lookup_type(+)         = 'INVOICE TYPE'
  AND fvl13.lookup_code(+)         = h2.AI_INVOICE_TYPE_LOOKUP_CODE
  AND fvl13.view_application_id(+) = 200
  AND fvl13.language(+)            = USERENV('LANG')
  
 ORDER BY event_id
;


--
CURSOR line_cur (x_first_event_id    in number, x_last_event_id    in number)
IS
SELECT  /*+ leading(xet) cardinality(xet,1) */
-- Event Class Code: CREDIT MEMOS
    xet.entity_id
   ,xet.legal_entity_id
   ,xet.entity_code
   ,xet.transaction_number
   ,xet.event_id
   ,xet.event_class_code
   ,xet.event_type_code
   ,xet.event_number
   ,xet.event_date
   ,xet.transaction_date
   ,xet.reference_num_1
   ,xet.reference_num_2
   ,xet.reference_num_3
   ,xet.reference_num_4
   ,xet.reference_char_1
   ,xet.reference_char_2
   ,xet.reference_char_3
   ,xet.reference_char_4
   ,xet.reference_date_1
   ,xet.reference_date_2
   ,xet.reference_date_3
   ,xet.reference_date_4
   ,xet.event_created_by
   ,xet.budgetary_control_flag
 , l1.LINE_NUMBER  
  , l1.AID_DESCRIPTION    source_1
  , l1.AID_DIST_CCID    source_2
  , l3.FEDERAL_ANTICIPATION    source_3
  , l3.FEDERAL_FUND_CATEGORY    source_4
  , l3.FEDERAL_FUND_EXPIRED_STATUS    source_5
  , l3.FEDERAL_FUND_CAT_DESC    source_6
  , l1.AID_LINE_TYPE_LOOKUP_CODE    source_8
  , fvl8.meaning   source_8_meaning
  , l3.FEDERAL_PRIOR_YEAR_FLAG    source_9
  , l1.ENCUMBRANCE_AMOUNT    source_10
  , l1.POD_ACCRUE_ON_RECEIPT_FLAG    source_11
  , fvl11.meaning   source_11_meaning
  , l1.PO_DISTRIBUTION_ID    source_12
  , l3.FEDERAL_ADJUSTMENT_TYPE    source_14
  , l1.DIST_ACCOUNT_REVERSAL_OPTION    source_15
  , l1.DISTRIBUTION_LINK_TYPE    source_17
  , l1.ALLOC_TO_MAIN_DIST_ID    source_19
  , l1.BUS_FLOW_AP_APP_ID    source_21
  , l1.BUS_FLOW_INV_DIST_TYPE    source_22
  , l1.BUS_FLOW_INV_ENTITY_CODE    source_23
  , l1.BUS_FLOW_INV_DIST_ID    source_24
  , l1.BUS_FLOW_INV_ID    source_25
  , l1.AID_INVOICE_DIST_ID    source_26
  , l1.UPG_ENC_CR_CCID    source_27
  , l1.UPG_ENC_CR_AMT    source_28
  , l1.UPG_ENC_CR_BASE_AMT    source_30
  , l1.UPG_ENC_DR_CCID    source_31
  , l1.UPG_ENC_DR_AMT    source_32
  , l1.UPG_ENC_DR_BASE_AMT    source_33
  , l1.UPG_AP_ENCUM_OPTION    source_34
  , l3.FEDERAL_ANTICIPATED_AMT    source_35
  , l1.DEFERRED_END_DATE    source_39
  , l1.DEFERRED_OPTION    source_40
  , l1.DEFERRED_START_DATE    source_41
  , l1.OVERRIDE_ACCTD_AMT_FLAG    source_42
  , fvl42.meaning   source_42_meaning
  , l1.AID_PARENT_REVERSAL_ID    source_46
  , l1.AID_STAT_AMOUNT    source_47
  , l4.TAX_LINE_ID    source_48
  , l5.REC_NREC_TAX_DIST_ID    source_49
  , l1.SUMMARY_TAX_LINE_ID    source_50
  , l1.UPG_CR_ENC_TYPE_ID    source_51
  , l1.UPG_DR_ENC_TYPE_ID    source_52
  , l1.ENCUMBRANCE_BASE_AMOUNT    source_53
  , l3.FEDERAL_UNANTICIPATED_AMT    source_54
  , l1.AID_AMOUNT    source_55
  , l1.INV_DIST_BASE_AMOUNT    source_56
  FROM xla_events_gt     xet 
  , AP_EXTRACT_INVOICE_DTLS_BC_V  l1
  , FV_XLA_AP_REF_V  l3
  , ZX_AP_DEF_TAX_EXTRACT_V  l4
  , ZX_AP_TAX_JRNL_LINE_DESC_V  l5
  , fnd_lookup_values    fvl8
  , fnd_lookup_values    fvl11
  , fnd_lookup_values    fvl42
 WHERE xet.event_id between x_first_event_id and x_last_event_id
   and xet.event_date between p_pad_start_date and p_pad_end_date
   and xet.event_class_code = C_EVENT_CLASS_CODE
   and xet.event_status_code <> 'N'   AND l1.event_id      = xet.event_id
 AND l3.Federal_Event_Id (+) = l1.event_Id  AND l3.Federal_line_number (+) = l1.line_number AND l1.ZRND_tax_dist_id = l4.REC_NREC_TAX_DIST_ID (+)  AND l1.ZRND_tax_dist_id = l5.REC_NREC_TAX_DIST_ID (+)    AND fvl8.lookup_type(+)         = 
'INVOICE DISTRIBUTION TYPE'
  AND fvl8.lookup_code(+)         = l1.AID_LINE_TYPE_LOOKUP_CODE
  AND fvl8.view_application_id(+) = 200
  AND fvl8.language(+)            = USERENV('LANG')
     AND fvl11.lookup_type(+)         = 'YES_NO'
  AND fvl11.lookup_code(+)         = l1.POD_ACCRUE_ON_RECEIPT_FLAG
  AND fvl11.view_application_id(+) = 0
  AND fvl11.language(+)            = USERENV('LANG')
     AND fvl42.lookup_type(+)         = 'YES_NO'
  AND fvl42.lookup_code(+)         = l1.OVERRIDE_ACCTD_AMT_FLAG
  AND fvl42.view_application_id(+) = 0
  AND fvl42.language(+)            = USERENV('LANG')
  ;

--
BEGIN
IF g_log_enabled THEN
   l_log_module := C_DEFAULT_MODULE||'.EventClass_60';
END IF;
IF (C_LEVEL_PROCEDURE >= g_log_level) THEN
   trace
      (p_msg      => 'BEGIN of EventClass_60'
      ,p_level    => C_LEVEL_PROCEDURE
      ,p_module   => l_log_module);
END IF;

IF (C_LEVEL_STATEMENT >= g_log_level) THEN
   trace
      (p_msg      => 'p_application_id = '||p_application_id||
                     ' - p_base_ledger_id = '||p_base_ledger_id||
                     ' - p_target_ledger_id  = '||p_target_ledger_id||
                     ' - p_language = '||p_language||
                     ' - p_currency_code = '||p_currency_code||
                     ' - p_sla_ledger_id = '||p_sla_ledger_id
      ,p_level    => C_LEVEL_STATEMENT
      ,p_module   => l_log_module);
END IF;
--
-- initialze arrays
--
g_array_event.DELETE;
l_rec_array_event := l_null_rec_array_event;
--
--------------------------------------
-- 4262811 Initialze MPA Line Number
--------------------------------------
XLA_AE_HEADER_PKG.g_mpa_line_num := 0;

--

--
OPEN header_cur;
--
IF (C_LEVEL_STATEMENT >= g_log_level) THEN
   trace
   (p_msg      => 'SQL - FETCH header_cur'
   ,p_level    => C_LEVEL_STATEMENT
   ,p_module   => l_log_module);
END IF;
--
LOOP
FETCH header_cur BULK COLLECT INTO
        l_array_entity_id
      , l_array_legal_entity_id
      , l_array_entity_code
      , l_array_transaction_num
      , l_array_event_id
      , l_array_class_code
      , l_array_event_type
      , l_array_event_number
      , l_array_event_date
      , l_array_transaction_date
      , l_array_reference_num_1
      , l_array_reference_num_2
      , l_array_reference_num_3
      , l_array_reference_num_4
      , l_array_reference_char_1
      , l_array_reference_char_2
      , l_array_reference_char_3
      , l_array_reference_char_4
      , l_array_reference_date_1
      , l_array_reference_date_2
      , l_array_reference_date_3
      , l_array_reference_date_4
      , l_array_event_created_by
      , l_array_budgetary_control_flag 
      , l_array_source_7
      , l_array_source_13
      , l_array_source_13_meaning
      , l_array_source_20
      , l_array_source_29
      , l_array_source_36
      , l_array_source_37
      , l_array_source_38
      , l_array_source_43
      , l_array_source_44
      , l_array_source_45
      , l_array_source_71
      , l_array_source_72
      , l_array_source_73
      LIMIT l_rows;
--
IF (C_LEVEL_EVENT >= g_log_level) THEN
   trace
   (p_msg      => '# rows extracted from header extract objects = '||TO_CHAR(header_cur%ROWCOUNT)
   ,p_level    => C_LEVEL_EVENT
   ,p_module   => l_log_module);
END IF;
--
EXIT WHEN l_array_entity_id.COUNT = 0;

-- initialize arrays
XLA_AE_HEADER_PKG.g_rec_header_new        := NULL;
XLA_AE_LINES_PKG.g_rec_lines              := NULL;

--
-- Bug 4458708
--
XLA_AE_LINES_PKG.g_LineNumber := 0;


-- 4262811 - when creating Accrual Reversal or MPA, use g_last_hdr_idx to increment for next header id
g_last_hdr_idx := l_array_event_id.LAST;
--
-- loop for the headers. Each iteration is for each header extract row
-- fetched in header cursor
--
FOR hdr_idx IN l_array_event_id.FIRST .. l_array_event_id.LAST LOOP

--
-- set event info as cache for other routines to refer event attributes
--
XLA_AE_JOURNAL_ENTRY_PKG.set_event_info
   (p_application_id           => p_application_id
   ,p_primary_ledger_id        => p_primary_ledger_id
   ,p_base_ledger_id           => p_base_ledger_id
   ,p_target_ledger_id         => p_target_ledger_id
   ,p_entity_id                => l_array_entity_id(hdr_idx)
   ,p_legal_entity_id          => l_array_legal_entity_id(hdr_idx)
   ,p_entity_code              => l_array_entity_code(hdr_idx)
   ,p_transaction_num          => l_array_transaction_num(hdr_idx)
   ,p_event_id                 => l_array_event_id(hdr_idx)
   ,p_event_class_code         => l_array_class_code(hdr_idx)
   ,p_event_type_code          => l_array_event_type(hdr_idx)
   ,p_event_number             => l_array_event_number(hdr_idx)
   ,p_event_date               => l_array_event_date(hdr_idx)
   ,p_transaction_date         => l_array_transaction_date(hdr_idx)
   ,p_reference_num_1          => l_array_reference_num_1(hdr_idx)
   ,p_reference_num_2          => l_array_reference_num_2(hdr_idx)
   ,p_reference_num_3          => l_array_reference_num_3(hdr_idx)
   ,p_reference_num_4          => l_array_reference_num_4(hdr_idx)
   ,p_reference_char_1         => l_array_reference_char_1(hdr_idx)
   ,p_reference_char_2         => l_array_reference_char_2(hdr_idx)
   ,p_reference_char_3         => l_array_reference_char_3(hdr_idx)
   ,p_reference_char_4         => l_array_reference_char_4(hdr_idx)
   ,p_reference_date_1         => l_array_reference_date_1(hdr_idx)
   ,p_reference_date_2         => l_array_reference_date_2(hdr_idx)
   ,p_reference_date_3         => l_array_reference_date_3(hdr_idx)
   ,p_reference_date_4         => l_array_reference_date_4(hdr_idx)
   ,p_event_created_by         => l_array_event_created_by(hdr_idx)
   ,p_budgetary_control_flag   => l_array_budgetary_control_flag(hdr_idx));

--
-- set the status of entry to C_VALID (0)
--
XLA_AE_JOURNAL_ENTRY_PKG.g_global_status    := XLA_AE_JOURNAL_ENTRY_PKG.C_VALID;

--
-- initialize a row for ae header
--
XLA_AE_HEADER_PKG.InitHeader(hdr_idx);

l_event_id := l_array_event_id(hdr_idx);

--
-- storing the hdr_idx for event. May be used by line cursor.
--
g_array_event(l_event_id).array_value_num('header_index') := hdr_idx;

--
-- store sources from header extract. This can be improved to
-- store only those sources from header extract that may be used in lines
--

g_array_event(l_event_id).array_value_char('source_7') := l_array_source_7(hdr_idx);
g_array_event(l_event_id).array_value_char('source_13') := l_array_source_13(hdr_idx);
g_array_event(l_event_id).array_value_char('source_13_meaning') := l_array_source_13_meaning(hdr_idx);
g_array_event(l_event_id).array_value_num('source_20') := l_array_source_20(hdr_idx);
g_array_event(l_event_id).array_value_char('source_29') := l_array_source_29(hdr_idx);
g_array_event(l_event_id).array_value_date('source_36') := l_array_source_36(hdr_idx);
g_array_event(l_event_id).array_value_num('source_37') := l_array_source_37(hdr_idx);
g_array_event(l_event_id).array_value_char('source_38') := l_array_source_38(hdr_idx);
g_array_event(l_event_id).array_value_num('source_43') := l_array_source_43(hdr_idx);
g_array_event(l_event_id).array_value_num('source_44') := l_array_source_44(hdr_idx);
g_array_event(l_event_id).array_value_char('source_45') := l_array_source_45(hdr_idx);
g_array_event(l_event_id).array_value_char('source_71') := l_array_source_71(hdr_idx);
g_array_event(l_event_id).array_value_num('source_72') := l_array_source_72(hdr_idx);
g_array_event(l_event_id).array_value_num('source_73') := l_array_source_73(hdr_idx);

--
-- initilaize the status of ae headers for diffrent balance types
-- the status is initialised to C_NOT_CREATED (2)
--
--g_array_event(l_event_id).array_value_num('actual_je_status') := XLA_AE_JOURNAL_ENTRY_PKG.C_NOT_CREATED;
--g_array_event(l_event_id).array_value_num('budget_je_status') := XLA_AE_JOURNAL_ENTRY_PKG.C_NOT_CREATED;
