--
-- PRIVATE PROCEDURE
--         insert_sources_61
--
----------------------------------------
--
PROCEDURE insert_sources_61(
                                p_target_ledger_id       IN NUMBER
                              , p_language               IN VARCHAR2
                              , p_sla_ledger_id          IN NUMBER
                              , p_pad_start_date         IN DATE
                              , p_pad_end_date           IN DATE
                         )
IS

C_EVENT_TYPE_CODE    CONSTANT  VARCHAR2(30)  := 'DEBIT MEMOS_ALL';
C_EVENT_CLASS_CODE   CONSTANT  VARCHAR2(30) := 'DEBIT MEMOS';
p_apps_owner                   VARCHAR2(30);
l_log_module                   VARCHAR2(240);
BEGIN
IF g_log_enabled THEN
      l_log_module := C_DEFAULT_MODULE||'.insert_sources_61';
END IF;
IF (C_LEVEL_PROCEDURE >= g_log_level) THEN

      trace
         (p_msg      => 'BEGIN of insert_sources_61'
         ,p_level    => C_LEVEL_PROCEDURE
         ,p_module   => l_log_module);

END IF;

-- select APPS owner
SELECT oracle_username
  INTO p_apps_owner
  FROM fnd_oracle_userid
 WHERE read_only_flag = 'U'
;

IF (C_LEVEL_STATEMENT >= g_log_level) THEN
      trace
         (p_msg      => 'p_target_ledger_id = '||p_target_ledger_id||
                        ' - p_language = '||p_language||
                        ' - p_sla_ledger_id  = '||p_sla_ledger_id ||
                        ' - p_pad_start_date = '||TO_CHAR(p_pad_start_date)||
                        ' - p_pad_end_date = '||TO_CHAR(p_pad_end_date)||
                        ' - p_apps_owner = '||TO_CHAR(p_apps_owner)
         ,p_level    => C_LEVEL_STATEMENT
         ,p_module   => l_log_module);
END IF;


--
INSERT INTO xla_diag_sources --hdr2
(
        event_id
      , ledger_id
      , sla_ledger_id
      , description_language
      , object_name
      , object_type_code
      , line_number
      , source_application_id
      , source_type_code
      , source_code
      , source_value
      , source_meaning
      , created_by
      , creation_date
      , last_update_date
      , last_updated_by
      , last_update_login
      , program_update_date
      , program_application_id
      , program_id
      , request_id
)
SELECT
        event_id
      , p_target_ledger_id
      , p_sla_ledger_id
      , p_language
      , object_name
      , object_type_code
      , line_number
      , source_application_id
      , source_type_code
      , source_code
      , SUBSTR(source_value ,1,1996)
      , SUBSTR(source_meaning ,1,200)
      , xla_environment_pkg.g_Usr_Id
      , TRUNC(SYSDATE)
      , TRUNC(SYSDATE)
      , xla_environment_pkg.g_Usr_Id
      , xla_environment_pkg.g_Login_Id
      , TRUNC(SYSDATE)
      , xla_environment_pkg.g_Prog_Appl_Id
      , xla_environment_pkg.g_Prog_Id
      , xla_environment_pkg.g_Req_Id
  FROM (
       SELECT xet.event_id                  event_id
            , 0                          line_number
            , CASE r
               WHEN 1 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 2 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 3 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 4 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 5 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 6 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 7 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 8 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 9 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 10 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 11 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 12 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                WHEN 13 THEN 'AP_INVOICE_EXTRACT_HEADER_V' 
                
               ELSE null
              END                           object_name
            , CASE r
                WHEN 1 THEN 'HEADER' 
                WHEN 2 THEN 'HEADER' 
                WHEN 3 THEN 'HEADER' 
                WHEN 4 THEN 'HEADER' 
                WHEN 5 THEN 'HEADER' 
                WHEN 6 THEN 'HEADER' 
                WHEN 7 THEN 'HEADER' 
                WHEN 8 THEN 'HEADER' 
                WHEN 9 THEN 'HEADER' 
                WHEN 10 THEN 'HEADER' 
                WHEN 11 THEN 'HEADER' 
                WHEN 12 THEN 'HEADER' 
                WHEN 13 THEN 'HEADER' 
                
                ELSE null
              END                           object_type_code
            , CASE r
                WHEN 1 THEN '200' 
                WHEN 2 THEN '200' 
                WHEN 3 THEN '200' 
                WHEN 4 THEN '200' 
                WHEN 5 THEN '200' 
                WHEN 6 THEN '200' 
                WHEN 7 THEN '200' 
                WHEN 8 THEN '200' 
                WHEN 9 THEN '200' 
                WHEN 10 THEN '200' 
                WHEN 11 THEN '200' 
                WHEN 12 THEN '200' 
                WHEN 13 THEN '200' 
                
                ELSE null
              END                           source_application_id
            , 'S'             source_type_code
            , CASE r
                WHEN 1 THEN 'AI_PAYMENT_STATUS_FLAG' 
                WHEN 2 THEN 'AI_INVOICE_TYPE_LOOKUP_CODE' 
                WHEN 3 THEN 'AI_INVOICE_ID' 
                WHEN 4 THEN 'AI_INVOICE_CURRENCY_CODE' 
                WHEN 5 THEN 'INV_EXCHANGE_DATE' 
                WHEN 6 THEN 'INV_EXCHANGE_RATE' 
                WHEN 7 THEN 'INV_EXCHANGE_RATE_TYPE' 
                WHEN 8 THEN 'AI_VENDOR_ID' 
                WHEN 9 THEN 'AI_VENDOR_SITE_ID' 
                WHEN 10 THEN 'THIRD_PARTY_TYPE' 
                WHEN 11 THEN 'INV_DOC_SEQUENCE_CATEGORY' 
                WHEN 12 THEN 'INV_DOC_SEQUENCE_IDENTIFIER' 
                WHEN 13 THEN 'INV_DOC_SEQUENCE_VALUE' 
                
                ELSE null
              END                           source_code
            , CASE r
                WHEN 1 THEN TO_CHAR(h2.AI_PAYMENT_STATUS_FLAG)
                WHEN 2 THEN TO_CHAR(h2.AI_INVOICE_TYPE_LOOKUP_CODE)
                WHEN 3 THEN TO_CHAR(h2.AI_INVOICE_ID)
                WHEN 4 THEN TO_CHAR(h2.AI_INVOICE_CURRENCY_CODE)
                WHEN 5 THEN TO_CHAR(h2.INV_EXCHANGE_DATE)
                WHEN 6 THEN TO_CHAR(h2.INV_EXCHANGE_RATE)
                WHEN 7 THEN TO_CHAR(h2.INV_EXCHANGE_RATE_TYPE)
                WHEN 8 THEN TO_CHAR(h2.AI_VENDOR_ID)
                WHEN 9 THEN TO_CHAR(h2.AI_VENDOR_SITE_ID)
                WHEN 10 THEN TO_CHAR(h2.THIRD_PARTY_TYPE)
                WHEN 11 THEN TO_CHAR(h2.INV_DOC_SEQUENCE_CATEGORY)
                WHEN 12 THEN TO_CHAR(h2.INV_DOC_SEQUENCE_IDENTIFIER)
                WHEN 13 THEN TO_CHAR(h2.INV_DOC_SEQUENCE_VALUE)
                
                ELSE null
              END                           source_value
            , CASE r
                WHEN 2 THEN fvl13.meaning
                
                ELSE null
              END               source_meaning
         FROM xla_events_gt     xet  
      , AP_INVOICE_EXTRACT_HEADER_V  h2
  , fnd_lookup_values    fvl13
             ,(select rownum r from all_objects where rownum <= 13 and owner = p_apps_owner)
         WHERE xet.event_date between p_pad_start_date AND p_pad_end_date
           AND xet.event_class_code = C_EVENT_CLASS_CODE
              AND h2.event_id = xet.event_id
   AND fvl13.lookup_type(+)         = 'INVOICE TYPE'
  AND fvl13.lookup_code(+)         = h2.AI_INVOICE_TYPE_LOOKUP_CODE
  AND fvl13.view_application_id(+) = 200
  AND fvl13.language(+)            = USERENV('LANG')
  
)
;
--
IF (C_LEVEL_STATEMENT >= g_log_level) THEN

      trace
         (p_msg      => 'number of header sources inserted = '||SQL%ROWCOUNT
         ,p_level    => C_LEVEL_STATEMENT
         ,p_module   => l_log_module);

END IF;
