                WHEN 10 THEN 'HEADER' 
                WHEN 11 THEN 'HEADER' 
                WHEN 12 THEN 'HEADER' 
                
                ELSE null
              END                           object_type_code
            , CASE r
                WHEN 1 THEN '200' 
                WHEN 2 THEN '200' 
                WHEN 3 THEN '200' 
                WHEN 4 THEN '200' 
                WHEN 5 THEN '200' 
                WHEN 6 THEN '200' 
                WHEN 7 THEN '200' 
