                WHEN 9 THEN '200' 
                WHEN 10 THEN '200' 
                WHEN 11 THEN '200' 
                WHEN 12 THEN '200' 
                
                ELSE null
              END                           source_application_id
            , 'S'             source_type_code
            , CASE r
                WHEN 1 THEN 'AI_INVOICE_TYPE_LOOKUP_CODE' 
                WHEN 2 THEN 'AI_INVOICE_ID' 
                WHEN 3 THEN 'AI_INVOICE_CURRENCY_CODE' 
                WHEN 4 THEN 'INV_EXCHANGE_DATE' 
                WHEN 5 THEN 'INV_EXCHANGE_RATE' 
                WHEN 6 THEN 'INV_EXCHANGE_RATE_TYPE' 
                WHEN 7 THEN 'AI_VENDOR_ID' 
                WHEN 8 THEN 'AI_VENDOR_SITE_ID' 
                WHEN 9 THEN 'THIRD_PARTY_TYPE' 
                WHEN 10 THEN 'INV_DOC_SEQUENCE_CATEGORY' 
                WHEN 11 THEN 'INV_DOC_SEQUENCE_IDENTIFIER' 
                WHEN 12 THEN 'INV_DOC_SEQUENCE_VALUE' 
                
                ELSE null
              END                           source_code
            , CASE r
                WHEN 1 THEN TO_CHAR(h2.AI_INVOICE_TYPE_LOOKUP_CODE)
                WHEN 2 THEN TO_CHAR(h2.AI_INVOICE_ID)
                WHEN 3 THEN TO_CHAR(h2.AI_INVOICE_CURRENCY_CODE)
