---------------------------------------
PROCEDURE AcctLineType_16 (
  p_application_id        IN NUMBER
 ,p_event_id              IN NUMBER
 ,p_calculate_acctd_flag  IN VARCHAR2
 ,p_calculate_g_l_flag    IN VARCHAR2
 ,p_actual_flag           IN OUT VARCHAR2
 ,p_balance_type_code     OUT VARCHAR2
 ,p_gain_or_loss_ref      OUT VARCHAR2
 
--Budget Account
 , p_source_11            IN NUMBER
--Main Or Backing Code
 , p_source_13            IN VARCHAR2
--Accounting Reversal Flag
 , p_source_16            IN VARCHAR2
--PO Distribution Identifier
 , p_source_29            IN NUMBER
--Entered Amount
 , p_source_33            IN NUMBER
--Currency Code
 , p_source_34            IN VARCHAR2
--Accounted Amount
 , p_source_35            IN NUMBER
--Allocated to Main Document Distribution Type
 , p_source_49            IN VARCHAR2
--Allocated to Main Document Distribution Identifier
 , p_source_51            IN NUMBER
--PO Header Identifier
