--JFMIP Reference
 , p_source_60            IN VARCHAR2
--PO Upgrade Encumbrance Type Identifier
 , p_source_61            IN NUMBER
--PO Release Identifier
 , p_source_66            IN NUMBER
)
IS

l_component_type              VARCHAR2(80);
