   l_rec_acct_attrs.array_acct_attr_code(10) := 'APPLIED_TO_ENTITY_CODE';
   l_rec_acct_attrs.array_char_value(10)  := p_source_25;
   l_rec_acct_attrs.array_acct_attr_code(11) := 'APPLIED_TO_FIRST_DIST_ID';
   l_rec_acct_attrs.array_num_value(11)  :=  to_char(p_source_26);
   l_rec_acct_attrs.array_acct_attr_code(12) := 'APPLIED_TO_FIRST_SYS_TRAN_ID';
   l_rec_acct_attrs.array_num_value(12)  :=  to_char(p_source_27);
   l_rec_acct_attrs.array_acct_attr_code(13) := 'APPLIED_TO_SECOND_DIST_ID';
   l_rec_acct_attrs.array_char_value(13)  := p_source_28;
   l_rec_acct_attrs.array_acct_attr_code(14) := 'DISTRIBUTION_IDENTIFIER_1';
   l_rec_acct_attrs.array_num_value(14)  :=  to_char(p_source_29);
   l_rec_acct_attrs.array_acct_attr_code(15) := 'DISTRIBUTION_IDENTIFIER_2';
   l_rec_acct_attrs.array_char_value(15)  := p_source_6;
   l_rec_acct_attrs.array_acct_attr_code(16) := 'DISTRIBUTION_TYPE';
   l_rec_acct_attrs.array_char_value(16)  := p_source_30;
   l_rec_acct_attrs.array_acct_attr_code(17) := 'ENC_UPG_CR_ACCT_CLASS';
   l_rec_acct_attrs.array_char_value(17)  := p_source_31;
   l_rec_acct_attrs.array_acct_attr_code(18) := 'ENC_UPG_CR_CCID';
   l_rec_acct_attrs.array_num_value(18)  := p_source_32;
   l_rec_acct_attrs.array_acct_attr_code(19) := 'ENC_UPG_CR_ENTERED_AMT';
   l_rec_acct_attrs.array_num_value(19)  := p_source_33;
   l_rec_acct_attrs.array_acct_attr_code(20) := 'ENC_UPG_CR_ENTERED_CURR';
   l_rec_acct_attrs.array_char_value(20)  := p_source_34;
   l_rec_acct_attrs.array_acct_attr_code(21) := 'ENC_UPG_CR_LEDGER_AMT';
   l_rec_acct_attrs.array_num_value(21)  := p_source_35;
   l_rec_acct_attrs.array_acct_attr_code(22) := 'ENC_UPG_DR_ACCT_CLASS';
   l_rec_acct_attrs.array_char_value(22)  := p_source_36;
   l_rec_acct_attrs.array_acct_attr_code(23) := 'ENC_UPG_DR_CCID';
   l_rec_acct_attrs.array_num_value(23)  := p_source_37;
   l_rec_acct_attrs.array_acct_attr_code(24) := 'ENC_UPG_DR_ENTERED_AMT';
   l_rec_acct_attrs.array_num_value(24)  := p_source_33;
   l_rec_acct_attrs.array_acct_attr_code(25) := 'ENC_UPG_DR_ENTERED_CURR';
   l_rec_acct_attrs.array_char_value(25)  := p_source_34;
   l_rec_acct_attrs.array_acct_attr_code(26) := 'ENC_UPG_DR_LEDGER_AMT';
   l_rec_acct_attrs.array_num_value(26)  := p_source_35;
   l_rec_acct_attrs.array_acct_attr_code(27) := 'ENC_UPG_OPTION';
   l_rec_acct_attrs.array_char_value(27)  := p_source_38;
   l_rec_acct_attrs.array_acct_attr_code(28) := 'ENTERED_CURRENCY_AMOUNT';
   l_rec_acct_attrs.array_num_value(28)  := p_source_33;
   l_rec_acct_attrs.array_acct_attr_code(29) := 'ENTERED_CURRENCY_CODE';
   l_rec_acct_attrs.array_char_value(29)  := p_source_34;
