   l_rec_acct_attrs.array_num_value(31)  :=  to_char(p_source_29);
   l_rec_acct_attrs.array_acct_attr_code(32) := 'REVERSED_DISTRIBUTION_ID2';
   l_rec_acct_attrs.array_char_value(32)  := p_source_6;
