   l_rec_acct_attrs.array_acct_attr_code(34) := 'UPG_CR_ENC_TYPE_ID';
   l_rec_acct_attrs.array_num_value(34)  := p_source_39;
   l_rec_acct_attrs.array_acct_attr_code(35) := 'UPG_DR_ENC_TYPE_ID';
   l_rec_acct_attrs.array_num_value(35)  := p_source_40;

   XLA_AE_LINES_PKG.SetLineAcctAttrs(l_rec_acct_attrs);
   p_gain_or_loss_ref  := XLA_AE_LINES_PKG.g_rec_lines.array_gain_or_loss_ref(XLA_AE_LINES_PKG.g_LineNumber);

   ---------------------------------------------------------------------------------------------------------------
