 , p_source_39            IN NUMBER
--Encumbrance Upgrade Debit Encumbrance Type
 , p_source_40            IN NUMBER
--Requisition Burden Applied to Application Identifier
 , p_source_41            IN NUMBER
--Requisition Distribution Type
 , p_source_42            IN VARCHAR2
--Requisition Burden Applied to Entity Code
 , p_source_43            IN VARCHAR2
--Requisition Burden Applied to First Distribution Identifier
 , p_source_44            IN NUMBER
--Requisition Burden Applied to First System Transaction Identifier
 , p_source_45            IN NUMBER
--Requisition Burden Applied to Second Distribution Identifier
 , p_source_46            IN VARCHAR2
--Backing Requisition Prevent Encumbrance Flag Flipped
 , p_source_67            IN VARCHAR2
)
IS

l_component_type              VARCHAR2(80);
l_component_code              VARCHAR2(30);
l_component_type_code         VARCHAR2(1);
l_component_appl_id           INTEGER;
l_amb_context_code            VARCHAR2(30);
l_entity_code                 VARCHAR2(30);
