   l_rec_acct_attrs.array_acct_attr_code(16) := 'DISTRIBUTION_TYPE';
   l_rec_acct_attrs.array_char_value(16)  := p_source_30;
   l_rec_acct_attrs.array_acct_attr_code(17) := 'ENC_UPG_CR_ACCT_CLASS';
