') =  'M' AND 
(NVL(p_source_14,'
') =  'O' OR 
NVL(p_source_14,'
') =  'N') AND 
NVL(p_source_9,'
') =  'PO' AND 
NVL(p_source_15,'
') =  'S'
 THEN 

   --
