 , p_source_71            IN NUMBER
--Project Encumbrance Applied to First System Transaction Identifier
 , p_source_72            IN NUMBER
--Project Encumbrance Applied to Second Distribution Identifier
 , p_source_73            IN VARCHAR2
)
IS

l_component_type              VARCHAR2(80);
l_component_code              VARCHAR2(30);
