EXCEPTION
  WHEN xla_exceptions_pkg.application_exception THEN
      RAISE;
  WHEN OTHERS THEN
       xla_exceptions_pkg.raise_message
           (p_location => 'XLA_00201_AAD_S_000006_BC_PKG.AcctLineType_71');
END AcctLineType_71;
