   l_rec_acct_attrs.array_acct_attr_code(11) := 'APPLIED_TO_FIRST_DIST_ID';
   l_rec_acct_attrs.array_num_value(11)  :=  to_char(p_source_26);
   l_rec_acct_attrs.array_acct_attr_code(12) := 'APPLIED_TO_FIRST_SYS_TRAN_ID';
   l_rec_acct_attrs.array_num_value(12)  :=  to_char(p_source_27);
   l_rec_acct_attrs.array_acct_attr_code(13) := 'APPLIED_TO_SECOND_DIST_ID';
   l_rec_acct_attrs.array_char_value(13)  := p_source_28;
   l_rec_acct_attrs.array_acct_attr_code(14) := 'DISTRIBUTION_IDENTIFIER_1';
