   l_rec_acct_attrs.array_char_value(20)  := p_source_34;
   l_rec_acct_attrs.array_acct_attr_code(21) := 'ENC_UPG_CR_LEDGER_AMT';
   l_rec_acct_attrs.array_num_value(21)  := p_source_35;
   l_rec_acct_attrs.array_acct_attr_code(22) := 'ENC_UPG_DR_ACCT_CLASS';
