      l_rec_rev_acct_attrs.array_char_value(19)  := l_array_source_30(Idx);
      l_rec_rev_acct_attrs.array_acct_attr_code(20) := 'UPG_CR_ENC_TYPE_ID';
      l_rec_rev_acct_attrs.array_num_value(20)  := l_array_source_39(Idx);
      l_rec_rev_acct_attrs.array_acct_attr_code(21) := 'UPG_DR_ENC_TYPE_ID';
      l_rec_rev_acct_attrs.array_num_value(21)  := l_array_source_40(Idx);


      xla_ae_lines_pkg.SetAcctReversalAttrs
