            , CASE r
                WHEN 1 THEN '201' 
                WHEN 2 THEN '201' 
                WHEN 3 THEN '201' 
                WHEN 4 THEN '201' 
                WHEN 5 THEN '201' 
                WHEN 6 THEN '201' 
                WHEN 7 THEN '201' 
                WHEN 8 THEN '201' 
                WHEN 9 THEN '201' 
                WHEN 10 THEN '201' 
                WHEN 11 THEN '201' 
                WHEN 12 THEN '201' 
                WHEN 13 THEN '201' 
                WHEN 14 THEN '201' 
                WHEN 15 THEN '201' 
                WHEN 16 THEN '201' 
                WHEN 17 THEN '201' 
                WHEN 18 THEN '201' 
                WHEN 19 THEN '201' 
                WHEN 20 THEN '201' 
                WHEN 21 THEN '201' 
                WHEN 22 THEN '201' 
                WHEN 23 THEN '201' 
                WHEN 24 THEN '201' 
                WHEN 25 THEN '201' 
                
                ELSE null
              END                           source_application_id
            , 'S'             source_type_code
            , CASE r
                WHEN 1 THEN 'JE_LINE_DESCRIPTION' 
                WHEN 2 THEN 'BUDGET_ACCOUNT' 
                WHEN 3 THEN 'MAIN_OR_BACKING_CODE' 
                WHEN 4 THEN 'ACCOUNTING_REVERSAL_FLAG' 
                WHEN 5 THEN 'PO_DISTRIBUTION_TYPE' 
                WHEN 6 THEN 'PO_DISTRIBUTION_ID' 
                WHEN 7 THEN 'ENTERED_AMT' 
                WHEN 8 THEN 'CURRENCY_CODE' 
                WHEN 9 THEN 'ACCOUNTED_AMT' 
                WHEN 10 THEN 'ALLOC_TO_DISTRIBUTION_TYPE' 
                WHEN 11 THEN 'ALLOC_TO_DIST_ID_1' 
                WHEN 12 THEN 'APPLIED_TO_APPL_ID' 
                WHEN 13 THEN 'APPLIED_TO_DIST_LINK_TYPE' 
                WHEN 14 THEN 'APPLIED_TO_ENTITY_CODE' 
                WHEN 15 THEN 'APPLIED_TO_DIST_ID_1' 
                WHEN 16 THEN 'APPLIED_TO_HEADER_ID_1' 
                WHEN 17 THEN 'DISTRIBUTION_LINK_TYPE' 
                WHEN 18 THEN 'PO_ENCUM_UPG_OPTION' 
                WHEN 19 THEN 'JFMIP_REFERENCE' 
                WHEN 20 THEN 'PO_UPG_ENC_TYPE_ID' 
                WHEN 21 THEN 'CURRENCY_CONVERSION_DATE' 
                WHEN 22 THEN 'CURRENCY_CONVERSION_RATE' 
                WHEN 23 THEN 'CURRENCY_CONVERSION_TYPE' 
                WHEN 24 THEN 'BACKING_REQ_ENC_CHANGED' 
                WHEN 25 THEN 'ADJUSTMENT_STATUS' 
                
                ELSE null
              END                           source_code
            , CASE r
                WHEN 1 THEN TO_CHAR(l1.JE_LINE_DESCRIPTION)
                WHEN 2 THEN TO_CHAR(l1.BUDGET_ACCOUNT)
                WHEN 3 THEN TO_CHAR(l1.MAIN_OR_BACKING_CODE)
                WHEN 4 THEN TO_CHAR(l1.ACCOUNTING_REVERSAL_FLAG)
                WHEN 5 THEN TO_CHAR(l1.PO_DISTRIBUTION_TYPE)
                WHEN 6 THEN TO_CHAR(l1.PO_DISTRIBUTION_ID)
                WHEN 7 THEN TO_CHAR(l1.ENTERED_AMT)
                WHEN 8 THEN TO_CHAR(l1.CURRENCY_CODE)
                WHEN 9 THEN TO_CHAR(l1.ACCOUNTED_AMT)
                WHEN 10 THEN TO_CHAR(l1.ALLOC_TO_DISTRIBUTION_TYPE)
                WHEN 11 THEN TO_CHAR(l1.ALLOC_TO_DIST_ID_1)
                WHEN 12 THEN TO_CHAR(l1.APPLIED_TO_APPL_ID)
                WHEN 13 THEN TO_CHAR(l1.APPLIED_TO_DIST_LINK_TYPE)
                WHEN 14 THEN TO_CHAR(l1.APPLIED_TO_ENTITY_CODE)
                WHEN 15 THEN TO_CHAR(l1.APPLIED_TO_DIST_ID_1)
                WHEN 16 THEN TO_CHAR(l1.APPLIED_TO_HEADER_ID_1)
                WHEN 17 THEN TO_CHAR(l1.DISTRIBUTION_LINK_TYPE)
                WHEN 18 THEN TO_CHAR(l1.PO_ENCUM_UPG_OPTION)
                WHEN 19 THEN TO_CHAR(l1.JFMIP_REFERENCE)
                WHEN 20 THEN TO_CHAR(l1.PO_UPG_ENC_TYPE_ID)
                WHEN 21 THEN TO_CHAR(l3.CURRENCY_CONVERSION_DATE)
                WHEN 22 THEN TO_CHAR(l3.CURRENCY_CONVERSION_RATE)
                WHEN 23 THEN TO_CHAR(l3.CURRENCY_CONVERSION_TYPE)
                WHEN 24 THEN TO_CHAR(l1.BACKING_REQ_ENC_CHANGED)
                WHEN 25 THEN TO_CHAR(l1.ADJUSTMENT_STATUS)
                
                ELSE null
              END                           source_value
            , null              source_meaning
         FROM  xla_events_gt     xet  
        , PO_EXTRACT_DETAIL_V  l1
        , PO_REQ_LINES_REF_V  l3
            , (select rownum r from all_objects where rownum <= 25 and owner = p_apps_owner)
        WHERE xet.event_date between p_pad_start_date AND p_pad_end_date
          AND xet.event_class_code = C_EVENT_CLASS_CODE
            AND l1.event_id          = xet.event_id
 AND l1.req_line_id = l3.req_line_id (+) 
)
;
--
IF (C_LEVEL_STATEMENT >= g_log_level) THEN

      trace
         (p_msg      => 'number of line sources inserted = '||SQL%ROWCOUNT
         ,p_level    => C_LEVEL_STATEMENT
         ,p_module   => l_log_module);

END IF;


IF (C_LEVEL_PROCEDURE >= g_log_level) THEN
      trace
         (p_msg      => 'END of insert_sources_86'
         ,p_level    => C_LEVEL_PROCEDURE
         ,p_module   => l_log_module);
END IF;
EXCEPTION
  WHEN xla_exceptions_pkg.application_exception THEN
      IF (C_LEVEL_EXCEPTION >= g_log_level) THEN
            trace
               (p_msg      => 'ERROR: XLA_CMP_COMPILER_ERROR = '||sqlerrm
               ,p_level    => C_LEVEL_EXCEPTION
               ,p_module   => l_log_module);
      END IF;
      RAISE;
  WHEN OTHERS THEN
      IF (C_LEVEL_EXCEPTION >= g_log_level) THEN
            trace
               (p_msg      => 'ERROR: XLA_CMP_COMPILER_ERROR = '||sqlerrm
               ,p_level    => C_LEVEL_EXCEPTION
               ,p_module   => l_log_module);
       END IF;
       xla_exceptions_pkg.raise_message
           (p_location => 'XLA_00201_AAD_S_000006_BC_PKG.insert_sources_86');
END insert_sources_86;
