TYPE t_array_source_7 IS TABLE OF PO_EXTRACT_DETAIL_V.JE_LINE_DESCRIPTION%TYPE INDEX BY BINARY_INTEGER;
TYPE t_array_source_11 IS TABLE OF PO_EXTRACT_DETAIL_V.BUDGET_ACCOUNT%TYPE INDEX BY BINARY_INTEGER;
