TYPE t_array_source_16 IS TABLE OF PO_EXTRACT_DETAIL_V.ACCOUNTING_REVERSAL_FLAG%TYPE INDEX BY BINARY_INTEGER;
TYPE t_array_source_24 IS TABLE OF PO_EXTRACT_DETAIL_V.PO_DISTRIBUTION_TYPE%TYPE INDEX BY BINARY_INTEGER;
TYPE t_array_source_29 IS TABLE OF PO_EXTRACT_DETAIL_V.PO_DISTRIBUTION_ID%TYPE INDEX BY BINARY_INTEGER;
TYPE t_array_source_33 IS TABLE OF PO_EXTRACT_DETAIL_V.ENTERED_AMT%TYPE INDEX BY BINARY_INTEGER;
TYPE t_array_source_34 IS TABLE OF PO_EXTRACT_DETAIL_V.CURRENCY_CODE%TYPE INDEX BY BINARY_INTEGER;
