INSERT INTO xla_diag_sources --hdr2
(
        event_id
      , ledger_id
      , sla_ledger_id
      , description_language
      , object_name
      , object_type_code
      , line_number
      , source_application_id
      , source_type_code
      , source_code
      , source_value
      , source_meaning
      , created_by
      , creation_date
      , last_update_date
      , last_updated_by
      , last_update_login
      , program_update_date
      , program_application_id
      , program_id
      , request_id
)
SELECT
        event_id
      , p_target_ledger_id
      , p_sla_ledger_id
      , p_language
      , object_name
      , object_type_code
      , line_number
      , source_application_id
      , source_type_code
      , source_code
      , SUBSTR(source_value ,1,1996)
      , SUBSTR(source_meaning ,1,200)
      , xla_environment_pkg.g_Usr_Id
      , TRUNC(SYSDATE)
      , TRUNC(SYSDATE)
      , xla_environment_pkg.g_Usr_Id
      , xla_environment_pkg.g_Login_Id
      , TRUNC(SYSDATE)
      , xla_environment_pkg.g_Prog_Appl_Id
      , xla_environment_pkg.g_Prog_Id
      , xla_environment_pkg.g_Req_Id
  FROM (
       SELECT xet.event_id                  event_id
            , 0                          line_number
            , CASE r
               WHEN 1 THEN 'PO_EXTRACT_HEADER_V' 
                WHEN 2 THEN 'PO_EXTRACT_HEADER_V' 
                WHEN 3 THEN 'PO_RELEASES_REF_V' 
                WHEN 4 THEN 'PO_EXTRACT_HEADER_V' 
                WHEN 5 THEN 'PO_EXTRACT_HEADER_V' 
                WHEN 6 THEN 'PO_EXTRACT_HEADER_V' 
                
               ELSE null
              END                           object_name
            , CASE r
                WHEN 1 THEN 'HEADER' 
                WHEN 2 THEN 'HEADER' 
                WHEN 3 THEN 'HEADER' 
                WHEN 4 THEN 'HEADER' 
                WHEN 5 THEN 'HEADER' 
                WHEN 6 THEN 'HEADER' 
                
                ELSE null
              END                           object_type_code
            , CASE r
                WHEN 1 THEN '201' 
                WHEN 2 THEN '201' 
                WHEN 3 THEN '201' 
                WHEN 4 THEN '201' 
                WHEN 5 THEN '201' 
                WHEN 6 THEN '201' 
                
                ELSE null
              END                           source_application_id
            , 'S'             source_type_code
            , CASE r
                WHEN 1 THEN 'PO_TYPE' 
                WHEN 2 THEN 'PO_NUMBER' 
                WHEN 3 THEN 'PO_RELEASE_NUMBER' 
                WHEN 4 THEN 'PO_RELEASE_ID' 
                WHEN 5 THEN 'GL_DATE' 
                WHEN 6 THEN 'ENCUMBRANCE_TYPE_ID' 
                
                ELSE null
              END                           source_code
            , CASE r
                WHEN 1 THEN TO_CHAR(h2.PO_TYPE)
                WHEN 2 THEN TO_CHAR(h2.PO_NUMBER)
                WHEN 3 THEN TO_CHAR(h3.PO_RELEASE_NUMBER)
                WHEN 4 THEN TO_CHAR(h2.PO_RELEASE_ID)
                WHEN 5 THEN TO_CHAR(h2.GL_DATE)
                WHEN 6 THEN TO_CHAR(h2.ENCUMBRANCE_TYPE_ID)
                
                ELSE null
              END                           source_value
            , null              source_meaning
         FROM xla_events_gt     xet  
      , PO_EXTRACT_HEADER_V  h2
      , PO_RELEASES_REF_V  h3
             ,(select rownum r from all_objects where rownum <= 6 and owner = p_apps_owner)
         WHERE xet.event_date between p_pad_start_date AND p_pad_end_date
           AND xet.event_class_code = C_EVENT_CLASS_CODE
              AND h2.event_id = xet.event_id
 AND h2.po_release_id = h3.po_release_id
)
;
--
IF (C_LEVEL_STATEMENT >= g_log_level) THEN

      trace
         (p_msg      => 'number of header sources inserted = '||SQL%ROWCOUNT
         ,p_level    => C_LEVEL_STATEMENT
         ,p_module   => l_log_module);

END IF;
