      l_rec_rev_acct_attrs.array_acct_attr_code(12) := 'ENC_UPG_DR_CCID';
      l_rec_rev_acct_attrs.array_num_value(12)  := l_array_source_37(Idx);
      l_rec_rev_acct_attrs.array_acct_attr_code(13) := 'ENC_UPG_DR_ENTERED_AMT';
