        l_array_duplicate_checker(line_record.event_id) := line_record.event_id;
	END IF;
END LOOP;

fnd_file.put_line(fnd_file.LOG, '***************************************************************************');
fnd_file.put_line(fnd_file.LOG, '                    ');


xla_exceptions_pkg.raise_message
      (p_location => 'XLA_00222_AAD_S_000006_PKG.EventClass_135');


WHEN OTHERS THEN
   xla_exceptions_pkg.raise_message
