  , h5.XLA_PARTY_TYPE    source_32
  , h5.CM_INVOICE_CURRENCY_CODE    source_44
  , h2.CM_BILL_CUST_ACCOUNT_ID    source_50
  , h1.CM_BILL_USES_SITE_USE_ID    source_51
  FROM xla_events_gt     xet 
  , AR_CM_BILL_SITE_USES_H_V  h1
  , AR_CM_BILL_TO_CUST_H_V  h2
  , AR_CREDIT_MEMO_H_V  h5
