INSERT INTO xla_diag_sources --hdr2
(
        event_id
      , ledger_id
      , sla_ledger_id
      , description_language
      , object_name
      , object_type_code
      , line_number
      , source_application_id
      , source_type_code
      , source_code
      , source_value
      , source_meaning
      , created_by
      , creation_date
      , last_update_date
      , last_updated_by
      , last_update_login
      , program_update_date
      , program_application_id
      , program_id
      , request_id
)
SELECT
        event_id
      , p_target_ledger_id
      , p_sla_ledger_id
      , p_language
      , object_name
      , object_type_code
      , line_number
      , source_application_id
      , source_type_code
      , source_code
      , SUBSTR(source_value ,1,1996)
      , SUBSTR(source_meaning ,1,200)
      , xla_environment_pkg.g_Usr_Id
      , TRUNC(SYSDATE)
      , TRUNC(SYSDATE)
      , xla_environment_pkg.g_Usr_Id
      , xla_environment_pkg.g_Login_Id
      , TRUNC(SYSDATE)
      , xla_environment_pkg.g_Prog_Appl_Id
      , xla_environment_pkg.g_Prog_Id
      , xla_environment_pkg.g_Req_Id
  FROM (
       SELECT xet.event_id                  event_id
            , 0                          line_number
            , CASE r
               WHEN 1 THEN 'AR_TRANSACTIONS_S_V' 
                WHEN 2 THEN 'AR_BILL_TO_CUSTOMERS_S_V' 
                WHEN 3 THEN 'AR_BILL_TO_SITE_USES_S_V' 
                WHEN 4 THEN 'AR_TRANSACTIONS_S_V' 
                WHEN 5 THEN 'AR_TRANSACTIONS_S_V' 
                WHEN 6 THEN 'AR_TRANSACTIONS_S_V' 
                WHEN 7 THEN 'AR_TRANSACTIONS_S_V' 
                
               ELSE null
              END                           object_name
            , CASE r
                WHEN 1 THEN 'HEADER' 
                WHEN 2 THEN 'HEADER' 
                WHEN 3 THEN 'HEADER' 
                WHEN 4 THEN 'HEADER' 
                WHEN 5 THEN 'HEADER' 
                WHEN 6 THEN 'HEADER' 
                WHEN 7 THEN 'HEADER' 
                
                ELSE null
              END                           object_type_code
            , CASE r
                WHEN 1 THEN '222' 
                WHEN 2 THEN '222' 
                WHEN 3 THEN '222' 
                WHEN 4 THEN '222' 
                WHEN 5 THEN '222' 
                WHEN 6 THEN '222' 
                WHEN 7 THEN '222' 
                
                ELSE null
              END                           source_application_id
            , 'S'             source_type_code
            , CASE r
                WHEN 1 THEN 'TRX_DOC_SEQUENCE_VALUE' 
                WHEN 2 THEN 'BILL_CUST_ACCOUNT_ID' 
                WHEN 3 THEN 'BILL_USES_SITE_USE_ID' 
                WHEN 4 THEN 'XLA_PARTY_TYPE' 
                WHEN 5 THEN 'TRX_INVOICE_CURRENCY_CODE' 
                WHEN 6 THEN 'TRX_DOC_SEQUENCE_CATEGORY' 
                WHEN 7 THEN 'TRX_DOC_SEQUENCE_ID' 
                
                ELSE null
              END                           source_code
            , CASE r
                WHEN 1 THEN TO_CHAR(h5.TRX_DOC_SEQUENCE_VALUE)
                WHEN 2 THEN TO_CHAR(h1.BILL_CUST_ACCOUNT_ID)
                WHEN 3 THEN TO_CHAR(h2.BILL_USES_SITE_USE_ID)
                WHEN 4 THEN TO_CHAR(h5.XLA_PARTY_TYPE)
                WHEN 5 THEN TO_CHAR(h5.TRX_INVOICE_CURRENCY_CODE)
                WHEN 6 THEN TO_CHAR(h5.TRX_DOC_SEQUENCE_CATEGORY)
                WHEN 7 THEN TO_CHAR(h5.TRX_DOC_SEQUENCE_ID)
                
                ELSE null
              END                           source_value
            , null              source_meaning
         FROM xla_events_gt     xet  
      , AR_BILL_TO_CUSTOMERS_S_V  h1
      , AR_BILL_TO_SITE_USES_S_V  h2
      , AR_TRANSACTIONS_S_V  h5
             ,(select rownum r from all_objects where rownum <= 7 and owner = p_apps_owner)
         WHERE xet.event_date between p_pad_start_date AND p_pad_end_date
           AND xet.event_class_code = C_EVENT_CLASS_CODE
              AND h1.event_id = xet.event_id
  AND h2.event_id  = h1.event_id
  AND h5.event_id  = h1.event_id

)
;
--
IF (C_LEVEL_STATEMENT >= g_log_level) THEN

      trace
         (p_msg      => 'number of header sources inserted = '||SQL%ROWCOUNT
         ,p_level    => C_LEVEL_STATEMENT
         ,p_module   => l_log_module);

END IF;
